			dt_suspensao_cpoe_w := cpoe_obter_dt_suspensao(nr_seq_dieta_cpoe_w, 'N');
			if	dt_suspensao_cpoe_w <= clock_timestamp() then
				ds_material_w := wheb_mensagem_pck.get_texto(820376) || ' ' || substr(ds_material_w,1,240);
			elsif (dt_suspensao_cpoe_w > clock_timestamp()) then
				ds_material_w := '(' || wheb_mensagem_pck.get_texto(1061446, 'DT_SUSPENSAO=' || TO_CHAR(dt_suspensao_cpoe_w, pkg_date_formaters.localize_mask('short', pkg_date_formaters.getUserLanguageTag(wheb_usuario_pck.get_cd_estabelecimento, wheb_usuario_pck.get_nm_usuario)))) || ') ' || substr(ds_material_w,1,240);
			end if;
			select	nextval('w_adep_t_seq')
			into STRICT	nr_seq_wadep_w
			;
			insert into w_adep_t(
				nr_sequencia,
				nm_usuario,
				ie_tipo_item,
				nr_prescricao,
				nr_seq_item,		
				cd_item,
				ds_item,
				ie_acm_sn,		
				cd_intervalo,
				qt_item,
				nr_agrupamento,
				ds_prescricao,
				ie_status_item,
				nr_seq_proc_interno,
				ie_diferenciado,
				ie_pendente_liberacao,
				ie_horario,
				cd_motivo_baixa,
				dt_prev_term,
				ie_vencida,
				ie_oncologia,
				nr_prescricoes,
				ie_regra_disp,
				nr_seq_cpoe,
				ie_item_ordem
				)
			values (
				nr_seq_wadep_w,
				nm_usuario_p,
				'SNE',
				current_setting('adep_gerar_horarios_pck.nr_prescricao_w')::prescr_medica.nr_prescricao%type,
				nr_seq_material_w,
				cd_material_w,
				substr(ds_material_w,1,240),
				ie_acm_sn_w,
				cd_intervalo_w,
				qt_dose_w,
				0,
				ds_prescricao_w,
				ie_status_solucao_w,
				0,
				'N',
				current_setting('adep_gerar_horarios_pck.ie_lib_pend_rep_w')::varchar(15),
				ie_horario_w,
				cd_motivo_baixa_w,
				dt_prev_fim_horario_w,
				ie_vencida_w,
				ie_oncologia_w,
				current_setting('adep_gerar_horarios_pck.nr_prescricao_w')::prescr_medica.nr_prescricao%type,
				ie_regra_disp_w,
				nr_seq_dieta_cpoe_w,
				ie_item_ordem_w
				);
			commit;
			if (ie_status_solucao_w = 'T') then
				PERFORM set_config('adep_gerar_horarios_pck.nr_prescricoes_sne_term_w', adep_juntar_prescricao(current_setting('adep_gerar_horarios_pck.nr_prescricoes_sne_term_w')::varchar(4000),current_setting('adep_gerar_horarios_pck.nr_prescricao_w')::prescr_medica.nr_prescricao%type), false);
			else
				PERFORM set_config('adep_gerar_horarios_pck.nr_prescricoes_sne_w', adep_juntar_prescricao(current_setting('adep_gerar_horarios_pck.nr_prescricoes_sne_w')::varchar(4000),current_setting('adep_gerar_horarios_pck.nr_prescricao_w')::prescr_medica.nr_prescricao%type), false);
			end if;
			end;
		end loop;
		close c02;	
		end;
	end if;
	END;

