SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE adep_gerar_horarios_pck.adep_sincronizar_leite ( cd_estabelecimento_p bigint, cd_setor_usuario_p bigint, cd_perfil_p bigint, nm_usuario_p text, nr_atendimento_p bigint, dt_inicial_horarios_p timestamp, dt_final_horarios_p timestamp, dt_validade_limite_p timestamp, ie_exibir_hor_realizados_p text, ie_exibir_hor_suspensos_p text, ie_regra_inclusao_p text, ie_data_lib_prescr_p text, ie_exibir_suspensos_p text, ie_agrupar_acm_sn_p text, dt_horario_p timestamp, nr_horario_p integer, ie_exibe_sem_lib_farm_p text, ie_prescr_setor_p text, cd_setor_paciente_p bigint) AS $body$
DECLARE

	ds_sep_bv_w		varchar(50);
	nr_seq_material_w	integer;
	nr_seq_horario_w	bigint;
	ie_status_horario_w	varchar(1);
	cd_material_w		integer;
	ds_material_w		varchar(255);
	ie_acm_sn_w		varchar(1);
	cd_intervalo_w		varchar(7);
	qt_dose_w		double precision;
	ds_prescricao_w		varchar(240);
	ie_status_w		varchar(1);
	ie_ciente_w		varchar(1);
	ds_comando_update_w	varchar(4000);
	current_setting('adep_gerar_horarios_pck.dt_horario_w')::timestamp		timestamp;
	nr_horario_w		integer;
	dt_fim_horario_w	timestamp;
	nr_seq_leite_deriv_w	bigint;
	ds_diluicao_w		varchar(2000);
	ie_horario_especial_w	varchar(1);
	ie_horario_adep_w	varchar(1);
	ie_recem_nato_w		varchar(1) := adep_gerar_horarios_pck.get_ie_recem_nato();
	nr_seq_dieta_cpoe_w	prescr_leite_deriv.nr_seq_dieta_cpoe%type;

	c01 CURSOR FOR	
	SELECT	a.nr_prescricao,
			c.nr_seq_material,
			c.nr_sequencia,
			CASE WHEN coalesce(c.dt_bloqueio::text, '') = '' THEN CASE WHEN SUBSTR(plt_obter_status_hor_supl_lib(c.dt_fim_horario,c.dt_suspensao,c.dt_lib_horario, c.nr_seq_processo),1,1)='N' THEN  CASE WHEN coalesce(c.ie_ciente,'N')='S' THEN 'W'  ELSE 'N' END   ELSE SUBSTR(plt_obter_status_hor_supl_lib(c.dt_fim_horario,c.dt_suspensao,c.dt_lib_horario, c.nr_seq_processo),1,1) END   ELSE 'B' END ,
			c.cd_material,
			obter_se_acm_sn(x.ie_acm,x.ie_se_necessario) ie_acm_sn,		
			x.cd_intervalo,
			x.qt_dose,
			substr(adep_obter_um_dosagem_prescr(a.nr_prescricao,c.nr_seq_material,x.ie_acm,x.ie_se_necessario),1,100) ds_prescricao,
			coalesce(x.ie_suspenso,'N') ie_suspenso,
			c.dt_horario,
			b.nr_sequencia,
			substr(obter_inf_dil_leite(x.nr_prescricao, x.nr_sequencia, x.nr_agrupamento, b.qt_vel_infusao, b.ie_via_aplicacao, b.qt_volume_total, b.qt_volume_oral, b.qt_volume_sonda, b.nr_seq_disp_succao, x.cd_intervalo),1,2000) ds_diluicao,
			b.nr_seq_dieta_cpoe,
			coalesce(c.ie_horario_adep,'S') ie_horario_adep,
			coalesce(c.ie_horario_especial,'N'),
			c.dt_fim_horario,
			c.ie_ciente
	from	prescr_leite_deriv b,
		prescr_material x,
		prescr_mat_hor c,
		prescr_medica a
	where	x.nr_prescricao		= c.nr_prescricao
	and	x.nr_sequencia		= c.nr_seq_material	
	and	x.nr_prescricao		= a.nr_prescricao
	and	c.nr_prescricao		= a.nr_prescricao	
	and	b.nr_prescricao		= x.nr_prescricao
	and	b.nr_sequencia		= x.nr_seq_leite_deriv
	and	a.nr_atendimento	= nr_atendimento_p
	and	a.dt_validade_prescr between dt_validade_limite_p and dt_fim_w
	and	x.ie_agrupador		= 16
	and	coalesce(c.ie_situacao,'A')	= 'A'
	and	coalesce(a.ie_adep,'S') = 'S'
	and	c.ie_agrupador		= 16
	and	((coalesce(a.ie_recem_nato,'N')	= 'N') or (ie_recem_nato_w = 'S'))		
	and	(((Obter_se_acm_sn_agora_especial(x.ie_acm, x.ie_se_necessario, x.ie_urgencia, c.ie_dose_especial) = 'N') and (c.dt_horario between dt_inicial_horarios_p and dt_final_horarios_p)) or
			 ((Obter_se_acm_sn_agora_especial(x.ie_acm, x.ie_se_necessario, x.ie_urgencia, c.ie_dose_especial) = 'S') and (obter_se_prescr_vig_adep(a.dt_prescricao,a.dt_validade_prescr,dt_inicial_horarios_p,dt_final_horarios_p) = 'S')))
	and	obter_se_exibir_rep_adep_setor(cd_setor_paciente_p,a.cd_setor_atendimento,coalesce(a.ie_adep,'S')) = 'S'
	and	((obter_se_prescr_lib_adep(a.dt_liberacao_medico, a.dt_liberacao, a.dt_liberacao_farmacia, ie_data_lib_prescr_p, a.ie_lib_farm) = 'S') or
		 ((ie_exibe_sem_lib_farm_p = 'S') and (coalesce(a.IE_PRESCR_NUTRICAO, 'N') = 'S')))
	and	((ie_exibir_suspensos_p = 'S') or (coalesce(x.dt_suspensao::text, '') = ''))
	and	coalesce(c.ie_situacao,'A')	= 'A'	
	and	((ie_exibir_hor_realizados_p = 'S') or (coalesce(c.dt_fim_horario::text, '') = ''))
	and	((ie_exibir_hor_suspensos_p = 'S') or (coalesce(c.dt_suspensao::text, '') = ''))
	and	((ie_regra_inclusao_p = 'S') or
		 ((ie_regra_inclusao_p = 'R') and (adep_obter_regra_inclusao(	'LD',
																		cd_estabelecimento_p, 
																		cd_setor_usuario_p, 
																		cd_perfil_p, 
																		c.cd_material, 
																		null, 
																		null, 
																		null,
																		a.cd_Setor_atendimento,
																		null,
																		a.nr_prescricao,
																		null) = 'S'))) -- nr_seq_exame_p
	and (c.dt_horario between dt_horario_inicio_sinc_w and dt_horario_fim_sinc_w  or (coalesce(c.ie_horario_especial,'N') = 'S'))
	and	((ie_prescr_setor_p = 'N') or (ie_prescr_setor_p = 'S' AND a.cd_setor_atendimento = cd_setor_paciente_p))
	and	Obter_se_horario_liberado(c.dt_lib_horario, c.dt_horario) = 'S'
	and	((get_ie_vigente = 'N') or (clock_timestamp() between a.dt_inicio_prescr and a.dt_validade_prescr + get_minutos_vigente/1440))
	group by
		a.nr_prescricao,
		c.nr_seq_material,
		c.nr_sequencia,
		c.dt_bloqueio,
		c.dt_fim_horario,
		c.dt_suspensao,
		c.cd_material,
		x.ie_acm,
		x.ie_se_necessario,		
		x.cd_intervalo,
		x.qt_dose,
		x.ie_suspenso,
		c.dt_lib_horario,
		c.dt_horario,
		b.nr_sequencia,
		b.qt_volume_total,
		substr(obter_inf_dil_leite(x.nr_prescricao, x.nr_sequencia, x.nr_agrupamento, b.qt_vel_infusao, b.ie_via_aplicacao, b.qt_volume_total, b.qt_volume_oral, b.qt_volume_sonda, b.nr_seq_disp_succao, x.cd_intervalo),1,2000),
		b.qt_volume_sonda,
		b.qt_volume_oral,
		b.ie_via_aplicacao,
		b.nr_seq_dieta_cpoe,
		c.ie_horario_adep,
		c.ie_horario_especial,
		c.dt_fim_horario,
		c.nr_seq_processo,
		c.ie_ciente
	order by
		c.dt_horario,
		c.dt_suspensao;		
	
BEGIN
	ds_sep_bv_w	:= obter_separador_bv;
	open c01;
	loop
	fetch c01 into	current_setting('adep_gerar_horarios_pck.nr_prescricao_w')::prescr_medica.nr_prescricao%type,
			nr_seq_material_w,
			nr_seq_horario_w,
			ie_status_horario_w,
			cd_material_w,
			ie_acm_sn_w,
			cd_intervalo_w,
			qt_dose_w,
			ds_prescricao_w,
			ie_status_w,
			current_setting('adep_gerar_horarios_pck.dt_horario_w')::timestamp,
			nr_seq_leite_deriv_w,
			ds_diluicao_w,
			nr_seq_dieta_cpoe_w,
			ie_horario_adep_w,
			ie_horario_especial_w,
			dt_fim_horario_w,
			ie_ciente_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		nr_horario_w := adep_gerar_horarios_pck.get_posicao_horario(current_setting('adep_gerar_horarios_pck.dt_horario_w')::timestamp);
		ds_comando_update_w	:=	' update w_adep_t ' ||
							' set nr_prescricoes = adep_juntar_prescricao(nr_prescricoes,:nr_prescricao), ' ||
							' ds_diluicao = :ds_dil, ' ||		
							' ie_ciencia = :ie_ciencia' ||							
							' where nm_usuario = :nm_usuario ' ||
							' and ie_tipo_item = :ie_tipo ' ||
							' and nvl(nr_prescricao,nvl(:nr_prescricao,0)) = nvl(:nr_prescricao,0) ' ||
							' and nvl(nr_seq_cpoe,nvl(:nr_seq_cpoe,0)) = nvl(:nr_seq_cpoe,0) ' ||
							' and nvl(nr_seq_leite_deriv,nvl(:nr_seq_leite_deriv,0)) = nvl(:nr_seq_leite_deriv,0) ' ||							
							' and nvl(nr_seq_item,nvl(:nr_seq_item,0)) = nvl(:nr_seq_item,0) ' ||
							' and cd_item = :cd_item ' ||
							' and nvl(cd_intervalo,0) = nvl(:cd_intervalo,0) ' ||
							' and nvl(qt_item,0) = nvl(:qt_item,0) ' ||
							' and ((ds_prescricao = :ds_prescricao) or (ds_prescricao is null)) ';
		CALL exec_sql_dinamico_bv('ADEP', ds_comando_update_w,	'nr_prescricao=' || to_char(current_setting('adep_gerar_horarios_pck.nr_prescricao_w')::prescr_medica.nr_prescricao%type) || ds_sep_bv_w ||
									'nr_seq_cpoe=' || to_char(nr_seq_dieta_cpoe_w) || ds_sep_bv_w ||
									'ds_dil=' || ds_diluicao_w || ds_sep_bv_w ||
									'nm_usuario=' || nm_usuario_p || ds_sep_bv_w ||
									'ie_tipo=LD' || ds_sep_bv_w ||
									'ie_ciencia=' || ie_ciente_w || ds_sep_bv_w ||
									'nr_seq_item='|| to_char(nr_seq_material_w) || ds_sep_bv_w ||
									'nr_seq_leite_deriv='|| to_char(nr_seq_leite_deriv_w) || ds_sep_bv_w ||										
									'cd_item=' || to_char(cd_material_w) || ds_sep_bv_w ||
									'cd_intervalo=' || cd_intervalo_w  || ds_sep_bv_w ||
									'qt_item=' || to_char(qt_dose_w) || ds_sep_bv_w ||
									'ds_prescricao=' || ds_prescricao_w || ds_sep_bv_w );
		commit;
		if (nr_horario_w IS NOT NULL AND nr_horario_w::text <> '') and (ie_horario_adep_w	= 'S') and
			((dt_fim_horario_w IS NOT NULL AND dt_fim_horario_w::text <> '') or (ie_horario_especial_w = 'N')) then
			ds_comando_update_w	:=	' update w_adep_t ' ||
							' set hora' || to_char(nr_horario_w) || ' = :vl_hora, ' ||
							' nr_prescricoes = adep_juntar_prescricao(nr_prescricoes,:nr_prescricao), ' ||
							' ds_diluicao = :ds_dil, ' ||
							' ie_ciencia = :ie_ciencia' ||							
							' where nm_usuario = :nm_usuario ' ||
							' and ie_tipo_item = :ie_tipo ' ||
							' and nvl(nr_prescricao,nvl(:nr_prescricao,0)) = nvl(:nr_prescricao,0) ' ||
							' and nvl(nr_seq_cpoe,nvl(:nr_seq_cpoe,0)) = nvl(:nr_seq_cpoe,0) ' ||
							' and nvl(nr_seq_leite_deriv,nvl(:nr_seq_leite_deriv,0)) = nvl(:nr_seq_leite_deriv,0) ' ||							
							' and nvl(nr_seq_item,nvl(:nr_seq_item,0)) = nvl(:nr_seq_item,0) ' ||
							' and cd_item = :cd_item ' ||
							' and nvl(cd_intervalo,0) = nvl(:cd_intervalo,0) ' ||
							' and nvl(qt_item,0) = nvl(:qt_item,0) ' ||
							' and ((ds_prescricao = :ds_prescricao) or (ds_prescricao is null)) ';
			CALL exec_sql_dinamico_bv('ADEP', ds_comando_update_w,	'vl_hora=S' || to_char(nr_seq_horario_w) || 'H' || ie_status_horario_w || ds_sep_bv_w ||
										'nr_prescricao=' || to_char(current_setting('adep_gerar_horarios_pck.nr_prescricao_w')::prescr_medica.nr_prescricao%type) || ds_sep_bv_w ||
										'nr_seq_cpoe=' || to_char(nr_seq_dieta_cpoe_w) || ds_sep_bv_w ||
										'ds_dil=' || ds_diluicao_w || ds_sep_bv_w ||
										'nm_usuario=' || nm_usuario_p || ds_sep_bv_w ||
										'ie_tipo=LD' || ds_sep_bv_w ||
										'ie_ciencia=' || ie_ciente_w || ds_sep_bv_w ||
										'nr_seq_item='|| to_char(nr_seq_material_w) || ds_sep_bv_w ||
										'nr_seq_leite_deriv='|| to_char(nr_seq_leite_deriv_w) || ds_sep_bv_w ||										
										'cd_item=' || to_char(cd_material_w) || ds_sep_bv_w ||
										'cd_intervalo=' || cd_intervalo_w  || ds_sep_bv_w ||
										'qt_item=' || to_char(qt_dose_w) || ds_sep_bv_w ||
										'ds_prescricao=' || ds_prescricao_w || ds_sep_bv_w );
			commit;
		end if;
		end;
	end loop;
	close c01;
	CALL Atualizar_adep_controle(nm_usuario_p, nr_atendimento_p, 'LD', 'N');
	END;	

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE adep_gerar_horarios_pck.adep_sincronizar_leite ( cd_estabelecimento_p bigint, cd_setor_usuario_p bigint, cd_perfil_p bigint, nm_usuario_p text, nr_atendimento_p bigint, dt_inicial_horarios_p timestamp, dt_final_horarios_p timestamp, dt_validade_limite_p timestamp, ie_exibir_hor_realizados_p text, ie_exibir_hor_suspensos_p text, ie_regra_inclusao_p text, ie_data_lib_prescr_p text, ie_exibir_suspensos_p text, ie_agrupar_acm_sn_p text, dt_horario_p timestamp, nr_horario_p integer, ie_exibe_sem_lib_farm_p text, ie_prescr_setor_p text, cd_setor_paciente_p bigint) FROM PUBLIC;
