CREATE OR REPLACE PROCEDURE adep_gerar_horarios_pck.adep_sincronizar_ordem_med ( cd_estabelecimento_p bigint, cd_setor_usuario_p bigint, cd_perfil_p bigint, nm_usuario_p text, nr_atendimento_p bigint, dt_inicial_horarios_p timestamp, dt_final_horarios_p timestamp, dt_validade_limite_p timestamp, ie_exibir_hor_realizados_p text, ie_exibir_hor_suspensos_p text, ie_regra_inclusao_p text, ie_data_lib_prescr_p text, ie_exibir_suspensos_p text, ie_agrupar_acm_sn_p text, dt_horario_p timestamp, nr_horario_p integer, ie_prescr_setor_p text, cd_setor_paciente_p bigint) AS $body$
DECLARE

	ds_sep_bv_w		varchar(50);				
	nr_seq_item_ordem_w	integer;
	nr_seq_horario_w	bigint;
	ie_status_horario_w	varchar(15);
	nr_seq_ordem_w		bigint;
	ds_ordem_w		varchar(255);
	ie_acm_sn_w		varchar(1);
	ds_prescricao_w		varchar(255);
	dt_suspensao_w		timestamp;
	ds_comando_update_w	varchar(4000);
	current_setting('adep_gerar_horarios_pck.dt_horario_w')::timestamp	timestamp;
	nr_horario_w	integer;
	ie_recem_nato_w		varchar(1) := adep_gerar_horarios_pck.get_ie_recem_nato();	
	c01 CURSOR FOR
	SELECT	a.nr_prescricao,
		b.nr_seq_solucao nr_seq_item_ordem,
		c.nr_sequencia,
		substr(obter_status_hor_ordem_med(c.dt_fim_horario,c.dt_suspensao),1,15),
		b.nr_sequencia nr_seq_ordem,
		x.ds_ordem,
		'N' ie_acm_sn,
		obter_desc_solucao_adep(a.nr_prescricao,b.nr_seq_solucao) ds_prescricao,
		c.dt_suspensao dt_suspensao,
		c.dt_horario
	from	ordem_medica x,
		prescr_ordem_hor c,
		prescr_medica_ordem b,
		prescr_medica a
	where	x.nr_sequencia = b.nr_seq_ordem
	and	c.nr_seq_ordem = b.nr_sequencia
	and	b.nr_prescricao = a.nr_prescricao
	and	obter_se_exibir_rep_adep_setor(cd_setor_paciente_p,a.cd_setor_atendimento,coalesce(a.ie_adep,'S')) = 'S'
	and	a.nr_atendimento = nr_atendimento_p
	and	((coalesce(a.ie_recem_nato,'N')	= 'N') or (ie_recem_nato_w = 'S'))	
	and	a.dt_validade_prescr between dt_validade_limite_p and dt_fim_w
	and	obter_se_prescr_lib_adep(a.dt_liberacao_medico, a.dt_liberacao, a.dt_liberacao_farmacia, ie_data_lib_prescr_p, a.ie_lib_farm) = 'S'
	and	(b.nr_seq_solucao IS NOT NULL AND b.nr_seq_solucao::text <> '')
	and	((ie_exibir_hor_realizados_p = 'S') or (coalesce(c.dt_fim_horario::text, '') = ''))
	and	((ie_exibir_hor_suspensos_p = 'S') or (coalesce(c.dt_suspensao::text, '') = ''))
	and	c.dt_horario between dt_horario_inicio_sinc_w and dt_horario_fim_sinc_w
	and	((ie_prescr_setor_p = 'N') or (ie_prescr_setor_p = 'S' AND a.cd_setor_atendimento = cd_setor_paciente_p))
	and	((get_ie_vigente = 'N') or (clock_timestamp() between a.dt_inicio_prescr and a.dt_validade_prescr + get_minutos_vigente/1440))
	and	coalesce(c.ie_horario_especial,'N') = 'N'
	group by
		c.dt_horario,
		a.nr_prescricao,
		b.nr_seq_solucao,
		c.nr_sequencia,
		c.dt_fim_horario,
		c.dt_suspensao,
		b.nr_sequencia,
		x.ds_ordem,
		'N'
	
union all

	SELECT	a.nr_prescricao,
		b.nr_seq_material nr_seq_item_ordem,
		c.nr_sequencia,
		substr(obter_status_hor_ordem_med(c.dt_fim_horario,c.dt_suspensao),1,15),
		b.nr_sequencia nr_seq_ordem,
		x.ds_ordem,
		'N' ie_acm_sn,
		z.ds_material ds_prescricao,
		c.dt_suspensao dt_suspensao,
		c.dt_horario
	from	material z,
		prescr_material y,
		ordem_medica x,
		prescr_ordem_hor c,
		prescr_medica_ordem b,
		prescr_medica a
	where	z.cd_material = y.cd_material
	and	y.nr_prescricao = a.nr_prescricao
	and	y.nr_prescricao = b.nr_prescricao
	and	y.nr_sequencia = b.nr_seq_material
	and	x.nr_sequencia = b.nr_seq_ordem
	and	c.nr_seq_ordem = b.nr_sequencia
	and	b.nr_prescricao = a.nr_prescricao
	and	coalesce(a.ie_adep,'S') = 'S'
	and	((coalesce(a.ie_recem_nato,'N')	= 'N') or (ie_recem_nato_w = 'S'))	
	and	a.nr_atendimento = nr_atendimento_p
	and	a.dt_validade_prescr between dt_validade_limite_p and dt_fim_w
	and	obter_se_prescr_lib_adep(a.dt_liberacao_medico, a.dt_liberacao, a.dt_liberacao_farmacia, ie_data_lib_prescr_p, a.ie_lib_farm) = 'S'
	and	(b.nr_seq_material IS NOT NULL AND b.nr_seq_material::text <> '')
	and	((ie_exibir_hor_realizados_p = 'S') or (coalesce(c.dt_fim_horario::text, '') = ''))
	and	((ie_exibir_hor_suspensos_p = 'S') or (coalesce(c.dt_suspensao::text, '') = ''))
	and	c.dt_horario between dt_horario_inicio_sinc_w and dt_horario_fim_sinc_w
	and	((ie_prescr_setor_p = 'N') or ((ie_prescr_setor_p = 'S') and (a.cd_setor_atendimento = Obter_Unidade_Atendimento(nr_atendimento_p, 'IAR', 'CS'))))
	and	((get_ie_vigente = 'N') or (clock_timestamp() between a.dt_inicio_prescr and a.dt_validade_prescr + get_minutos_vigente/1440))
	and	coalesce(c.ie_horario_especial,'N') = 'N'
	group by
		c.dt_horario,
		a.nr_prescricao,
		b.nr_seq_material,
		c.nr_sequencia,
		c.dt_fim_horario,
		c.dt_suspensao,
		b.nr_sequencia,
		x.ds_ordem,
		'N',
		z.ds_material
	
union all

	select	a.nr_prescricao,
			b.nr_seq_nut_pac nr_seq_item_ordem,
            c.nr_sequencia nr_seq_horario,
			substr(obter_status_hor_ordem_med(c.dt_fim_horario,c.dt_suspensao),1,15),
			b.nr_sequencia nr_seq_ordem,
			x.ds_ordem,
			'N' ie_acm_sn,
            (case
                when z.ie_npt_adulta = 'S' 
                then CASE WHEN z.ie_forma='P' THEN  obter_desc_expressao(296585) || ' ' || y.ds_npt  ELSE obter_valor_dominio(1988,z.ie_forma) END 
                else obter_desc_expressao(305336) || ' ' || z.hr_prim_horario end) ds_prescricao,
			c.dt_suspensao dt_suspensao,
            c.dt_horario
		FROM ordem_medica x, prescr_ordem_hor c, prescr_medica_ordem b, prescr_medica a, nut_pac z
LEFT OUTER JOIN protocolo_npt y ON (z.nr_seq_protocolo = y.nr_sequencia)
WHERE x.nr_sequencia = b.nr_seq_ordem and z.nr_prescricao = a.nr_prescricao  and c.nr_seq_ordem = b.nr_sequencia and b.nr_prescricao = a.nr_prescricao and obter_se_exibir_rep_adep_setor(cd_setor_paciente_p,a.cd_setor_atendimento,coalesce(a.ie_adep,'S')) = 'S' and a.nr_atendimento = nr_atendimento_p and a.dt_validade_prescr between dt_validade_limite_p and dt_fim_w and obter_se_prescr_lib_adep(a.dt_liberacao_medico, a.dt_liberacao, a.dt_liberacao_farmacia, ie_data_lib_prescr_p, a.ie_lib_farm) = 'S' and ((coalesce(a.ie_recem_nato,'N') = 'N') or (ie_recem_nato_w = 'S')) and (('N' = 'N' AND c.dt_horario between dt_inicial_horarios_p and dt_final_horarios_p) or
			 (('N' = 'S') and (obter_se_prescr_vig_adep(a.dt_prescricao,a.dt_validade_prescr,dt_inicial_horarios_p,dt_final_horarios_p) = 'S'))) and ((ie_exibir_hor_realizados_p = 'S') or (coalesce(c.dt_fim_horario::text, '') = '')) and ((ie_exibir_hor_suspensos_p = 'S') or (coalesce(c.dt_suspensao::text, '') = '')) and ((ie_prescr_setor_p = 'N') or (ie_prescr_setor_p = 'S' AND a.cd_setor_atendimento = cd_setor_paciente_p)) and ((get_ie_vigente = 'N') or (clock_timestamp() between a.dt_inicio_prescr and a.dt_validade_prescr + get_minutos_vigente/1440)) and (b.nr_seq_nut_pac IS NOT NULL AND b.nr_seq_nut_pac::text <> '') group by
			a.nr_prescricao,
			b.nr_seq_nut_pac,
            c.nr_sequencia,
			c.dt_fim_horario,
            c.dt_suspensao,
			b.nr_sequencia,
			x.ds_ordem,
			'N',
            z.ie_npt_adulta,
            z.ie_forma,
            y.ds_npt,
            z.hr_prim_horario,
            c.dt_horario
	
union all

    select	a.nr_prescricao,
			b.nr_seq_dieta_enteral nr_seq_item_ordem,
            c.nr_sequencia nr_seq_horario,
            substr(obter_status_hor_ordem_med(c.dt_fim_horario,c.dt_suspensao),1,15),
			b.nr_sequencia nr_seq_ordem,
			x.ds_ordem,
			'N' ie_acm_sn,
			z.ds_material ds_prescricao,
			c.dt_suspensao dt_suspensao,
            c.dt_horario
		from	material z,
			prescr_material y,
			ordem_medica x,
			prescr_ordem_hor c,
			prescr_medica_ordem b,
			prescr_medica a
		where	z.cd_material = y.cd_material
		and	y.nr_prescricao = a.nr_prescricao
		and	y.nr_prescricao = b.nr_prescricao
		and	y.nr_sequencia = b.nr_seq_dieta_enteral
		and	x.nr_sequencia = b.nr_seq_ordem
		and	c.nr_seq_ordem = b.nr_sequencia
		and	b.nr_prescricao = a.nr_prescricao
		and	a.nr_atendimento = nr_atendimento_p
		and	a.dt_validade_prescr between dt_validade_limite_p and dt_fim_w
		and	obter_se_prescr_lib_adep(a.dt_liberacao_medico, a.dt_liberacao, a.dt_liberacao_farmacia, ie_data_lib_prescr_p, a.ie_lib_farm) = 'S'
		and	((coalesce(a.ie_recem_nato,'N') = 'N') or (ie_recem_nato_w = 'S'))			
		and	(('N' = 'N' AND c.dt_horario between dt_inicial_horarios_p and dt_final_horarios_p) or
			 (('N' = 'S') and (obter_se_prescr_vig_adep(a.dt_prescricao,a.dt_validade_prescr,dt_inicial_horarios_p,dt_final_horarios_p) = 'S')))
		and	((ie_exibir_hor_realizados_p = 'S') or (coalesce(c.dt_fim_horario::text, '') = ''))
		and	((ie_exibir_hor_suspensos_p = 'S') or (coalesce(c.dt_suspensao::text, '') = ''))
		and	((ie_prescr_setor_p = 'N') or (ie_prescr_setor_p = 'S' AND a.cd_setor_atendimento = cd_setor_paciente_p))
		and	((get_ie_vigente = 'N') or (clock_timestamp() between a.dt_inicio_prescr and a.dt_validade_prescr + get_minutos_vigente/1440))
		and	(b.nr_seq_dieta_enteral IS NOT NULL AND b.nr_seq_dieta_enteral::text <> '')
		group by
			a.nr_prescricao,
			b.nr_seq_dieta_enteral,
            c.nr_sequencia,
            c.dt_fim_horario,
            c.dt_suspensao,
			b.nr_sequencia,
			x.ds_ordem,
			'N',
			z.ds_material,
            c.dt_horario
	
union all

    select	a.nr_prescricao,
			b.nr_seq_gasoterapia nr_seq_item_ordem,
            c.nr_sequencia nr_seq_horario,
            substr(obter_status_hor_ordem_med(c.dt_fim_horario,c.dt_suspensao),1,15),
			b.nr_sequencia nr_seq_ordem,
			x.ds_ordem,
			'N' ie_acm_sn,
			z.ds_gas ds_prescricao,
            c.dt_suspensao dt_suspensao,
            c.dt_horario
		from	ordem_medica x,
			prescr_ordem_hor c,
			prescr_medica_ordem b,
			prescr_medica a,
            prescr_gasoterapia y,
            gas z
		where	x.nr_sequencia = b.nr_seq_ordem
		and	c.nr_seq_ordem = b.nr_sequencia
		and	b.nr_prescricao = a.nr_prescricao
        and a.nr_prescricao = y.nr_prescricao
        and z.nr_sequencia = y.nr_seq_gas
		and	a.nr_atendimento = nr_atendimento_p
		and	a.dt_validade_prescr between dt_validade_limite_p and dt_fim_w
		and	obter_se_prescr_lib_adep(a.dt_liberacao_medico, a.dt_liberacao, a.dt_liberacao_farmacia, ie_data_lib_prescr_p, a.ie_lib_farm) = 'S'
		and	((coalesce(a.ie_recem_nato,'N') = 'N') or (ie_recem_nato_w = 'S'))			
		and	(('N' = 'N' AND c.dt_horario between dt_inicial_horarios_p and dt_final_horarios_p) or
			 (('N' = 'S') and (obter_se_prescr_vig_adep(a.dt_prescricao,a.dt_validade_prescr,dt_inicial_horarios_p,dt_final_horarios_p) = 'S')))
		and	((ie_exibir_hor_realizados_p = 'S') or (coalesce(c.dt_fim_horario::text, '') = ''))
		and	((ie_exibir_hor_suspensos_p = 'S') or (coalesce(c.dt_suspensao::text, '') = ''))
		and	((ie_prescr_setor_p = 'N') or (ie_prescr_setor_p = 'S' AND a.cd_setor_atendimento = cd_setor_paciente_p))
		and	((get_ie_vigente = 'N') or (clock_timestamp() between a.dt_inicio_prescr and a.dt_validade_prescr + get_minutos_vigente/1440))
		and	(b.nr_seq_gasoterapia IS NOT NULL AND b.nr_seq_gasoterapia::text <> '')
		group by
			a.nr_prescricao,
			b.nr_seq_gasoterapia,
            c.nr_sequencia,
            c.dt_fim_horario,
            c.dt_suspensao,
			b.nr_sequencia,
			x.ds_ordem,
			'N',
			z.ds_gas,
            c.dt_horario
        
union all

        select	a.nr_prescricao,
			b.nr_seq_hemoterapia nr_seq_item_ordem,
			c.nr_sequencia nr_seq_horario,
            substr(obter_status_hor_ordem_med(c.dt_fim_horario,c.dt_suspensao),1,15),
            b.nr_sequencia nr_seq_ordem,
			x.ds_ordem,
			'N' ie_acm_sn,
            CASE WHEN coalesce(y.nr_seq_exame_sangue::text, '') = '' THEN CASE WHEN coalesce(y.ie_util_hemocomponente::text, '') = '' THEN null  ELSE '('|| substr(obter_valor_dominio(2247,y.ie_util_hemocomponente),1,20) ||') ' END  || coalesce(e.ds_derivado,d.ds_procedimento)  ELSE substr(Obter_desc_san_exame(y.nr_seq_exame_sangue),1,80) END  ds_prescricao,
			c.dt_suspensao dt_suspensao,
            c.dt_horario
		FROM ordem_medica x, procedimento d, prescr_ordem_hor c, prescr_medica_ordem b, prescr_medica a, prescr_procedimento y
LEFT OUTER JOIN san_derivado e ON (y.nr_seq_derivado = e.nr_sequencia)
WHERE x.nr_sequencia = b.nr_seq_ordem  and y.cd_procedimento = d.cd_procedimento and y.ie_origem_proced = d.ie_origem_proced and c.nr_seq_ordem = b.nr_sequencia and b.nr_prescricao = a.nr_prescricao and y.nr_prescricao = a.nr_prescricao and (b.nr_seq_hemoterapia IS NOT NULL AND b.nr_seq_hemoterapia::text <> '') and (y.nr_seq_solic_sangue IS NOT NULL AND y.nr_seq_solic_sangue::text <> '') and coalesce(y.nr_seq_exame::text, '') = '' and y.nr_sequencia = b.nr_seq_hemoterapia and a.nr_atendimento = nr_atendimento_p and a.dt_validade_prescr between dt_validade_limite_p and dt_fim_w and obter_se_prescr_lib_adep(a.dt_liberacao_medico, a.dt_liberacao, a.dt_liberacao_farmacia, ie_data_lib_prescr_p, a.ie_lib_farm) = 'S' and ((coalesce(a.ie_recem_nato,'N') = 'N') or (ie_recem_nato_w = 'S')) and (('N' = 'N' AND c.dt_horario between dt_inicial_horarios_p and dt_final_horarios_p) or
			 (('N' = 'S') and (obter_se_prescr_vig_adep(a.dt_prescricao,a.dt_validade_prescr,dt_inicial_horarios_p,dt_final_horarios_p) = 'S'))) and ((ie_exibir_hor_realizados_p = 'S') or (coalesce(c.dt_fim_horario::text, '') = '')) and ((ie_exibir_hor_suspensos_p = 'S') or (coalesce(c.dt_suspensao::text, '') = '')) and ((ie_prescr_setor_p = 'N') or (ie_prescr_setor_p = 'S' AND a.cd_setor_atendimento = cd_setor_paciente_p)) and ((get_ie_vigente = 'N') or (clock_timestamp() between a.dt_inicio_prescr and a.dt_validade_prescr + get_minutos_vigente/1440)) group by
			a.nr_prescricao,
			b.nr_seq_hemoterapia,
			c.nr_sequencia,
            c.dt_fim_horario,
            c.dt_suspensao,
            b.nr_sequencia,
			x.ds_ordem,
			'N',
            y.nr_seq_exame_sangue,
            y.ie_util_hemocomponente,
            e.ds_derivado,
            d.ds_procedimento,
            c.dt_horario					
	order by
		dt_horario,
		dt_suspensao;
	
BEGIN
	ds_sep_bv_w := obter_separador_bv;
	open c01;
	loop
	fetch c01 into	current_setting('adep_gerar_horarios_pck.nr_prescricao_w')::prescr_medica.nr_prescricao%type,
			nr_seq_item_ordem_w,
			nr_seq_horario_w,
			ie_status_horario_w,
			nr_seq_ordem_w,
			ds_ordem_w,
			ie_acm_sn_w,
			ds_prescricao_w,
			dt_suspensao_w,
			current_setting('adep_gerar_horarios_pck.dt_horario_w')::timestamp;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		nr_horario_w := adep_gerar_horarios_pck.get_posicao_horario( current_setting('adep_gerar_horarios_pck.dt_horario_w')::timestamp );
		if (nr_horario_w IS NOT NULL AND nr_horario_w::text <> '') then
			ds_comando_update_w	:=	' update w_adep_t ' ||
							' set hora' || to_char(nr_horario_w) || ' = :vl_hora, ' ||
							' nr_prescricoes = adep_juntar_prescricao(nr_prescricoes,:nr_prescricao) ' ||
							' where nm_usuario = :nm_usuario ' ||
							' and ie_tipo_item = :ie_tipo ' ||
							' and nvl(nr_prescricao,nvl(:nr_prescricao,0)) = nvl(:nr_prescricao,0) ' ||
							' and nvl(nr_seq_item,nvl(:nr_seq_item,0)) = nvl(:nr_seq_item,0) ' ||					
							' and cd_item = :cd_item ' ||
							' and ((ds_prescricao = :ds_prescricao) or (ds_prescricao is null)) ';
			CALL exec_sql_dinamico_bv('ADEP', ds_comando_update_w,	'vl_hora=S' || to_char(nr_seq_horario_w) || 'H' || ie_status_horario_w || ds_sep_bv_w ||
										'nr_prescricao=' || to_char(current_setting('adep_gerar_horarios_pck.nr_prescricao_w')::prescr_medica.nr_prescricao%type) || ds_sep_bv_w ||
										'nm_usuario=' || nm_usuario_p || ds_sep_bv_w || 
										'ie_tipo=B' || ds_sep_bv_w ||
										'nr_seq_item=' || to_char(nr_seq_item_ordem_w) || ds_sep_bv_w ||
										'cd_item=' || to_char(nr_seq_horario_w) || ds_sep_bv_w ||
										'ds_prescricao=' || ds_prescricao_w || ds_sep_bv_w );
			commit;
		end if;
		end;
	end loop;
	close c01;
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE adep_gerar_horarios_pck.adep_sincronizar_ordem_med ( cd_estabelecimento_p bigint, cd_setor_usuario_p bigint, cd_perfil_p bigint, nm_usuario_p text, nr_atendimento_p bigint, dt_inicial_horarios_p timestamp, dt_final_horarios_p timestamp, dt_validade_limite_p timestamp, ie_exibir_hor_realizados_p text, ie_exibir_hor_suspensos_p text, ie_regra_inclusao_p text, ie_data_lib_prescr_p text, ie_exibir_suspensos_p text, ie_agrupar_acm_sn_p text, dt_horario_p timestamp, nr_horario_p integer, ie_prescr_setor_p text, cd_setor_paciente_p bigint) FROM PUBLIC;
