CREATE OR REPLACE PROCEDURE adep_gerar_horarios_pck.adep_sincronizar_proced_assoc ( cd_estabelecimento_p bigint, cd_setor_usuario_p bigint, cd_perfil_p bigint, nm_usuario_p text, nr_atendimento_p bigint, dt_inicial_horarios_p timestamp, dt_final_horarios_p timestamp, dt_validade_limite_p timestamp, ie_exibir_hor_realizados_p text, ie_exibir_hor_suspensos_p text, ie_regra_inclusao_p text, ie_data_lib_prescr_p text, ie_exibir_suspensos_p text, ie_agrupar_acm_sn_p text, ie_agrupar_associados_p text, ie_data_lib_proced_p text, ie_so_proc_setor_usuario_p text, dt_horario_p timestamp, nr_horario_p integer, ie_prescr_setor_p text, cd_setor_paciente_p bigint, ie_palm_p text) AS $body$
DECLARE
				
	ds_sep_bv_w		varchar(50);				
	nr_seq_procedimento_w	numeric(20);
	nr_seq_horario_w	bigint;
	ie_status_horario_w	varchar(15);
	cd_procedimento_w	bigint;
	ds_procedimento_w	varchar(255);
	nr_seq_proc_interno_w	bigint;
	ie_acm_sn_w		varchar(1);
	cd_intervalo_w		varchar(7);
	qt_procedimento_w	double precision;
	ds_prescricao_w		varchar(240);
	ds_mat_med_assoc_w	varchar(2000);
	ie_suspenso_w		varchar(1);
	dt_suspensao_w		timestamp;
	ds_comando_update_w	varchar(4000);
	current_setting('adep_gerar_horarios_pck.dt_horario_w')::timestamp		timestamp;
	nr_horario_w		integer;
	ie_recem_nato_w		varchar(1) := adep_gerar_horarios_pck.get_ie_recem_nato();
	ds_observacao_w		varchar(2000);
	x varchar(100);
	nr_seq_proc_cpoe_w	prescr_procedimento.nr_seq_proc_cpoe%type;
	c01 CURSOR FOR	
	SELECT	a.nr_prescricao,
			CASE WHEN substr(obter_se_associado_dif(a.nr_prescricao,x.nr_sequencia),1,1)='N' THEN x.nr_sequencia  ELSE x.nr_sequencia END ,
			c.nr_sequencia,
			CASE WHEN coalesce(c.dt_primeira_checagem::text, '') = '' THEN substr(obter_status_hor_proced2(c.dt_fim_horario,c.dt_suspensao,c.ie_dose_especial,c.dt_inicio_horario),1,15)  ELSE 'Q' END ,
			x.cd_procedimento,
			y.ds_procedimento,
			x.nr_seq_proc_interno,
			obter_se_acm_sn(x.ie_acm,x.ie_se_necessario) ie_acm_sn,		
			x.cd_intervalo,
			x.qt_procedimento,
			substr(adep_obter_dados_prescr_proc(a.nr_prescricao,x.nr_sequencia,'QIL','S',x.ie_acm,x.ie_se_necessario),1,100) ds_prescricao,
			substr(x.ds_observacao || '  ' || CASE WHEN coalesce(coalesce(x.nr_seq_prescr_mat,x.nr_seq_prescr_sol)::text, '') = '' THEN obter_mat_med_assoc_proc(a.nr_prescricao,x.nr_sequencia,'S')  ELSE '' END  || '  ' || Obter_desc_item_assoc_proc(coalesce(x.nr_seq_prescr_mat,nr_seq_prescr_sol),x.nr_prescricao,CASE WHEN coalesce(nr_seq_prescr_mat::text, '') = '' THEN 'S'  ELSE 'M' END ) ,1,2000) ds_mat_med_assoc,
			CASE WHEN x.ie_suspenso='S' THEN  x.ie_suspenso  ELSE null END  ie_suspenso,
			c.dt_suspensao dt_suspensao,
			c.dt_horario,
			CASE WHEN GetIEObsProced='S' THEN substr(x.ds_observacao,1,2000)  ELSE null END  ds_observacao,
			x.nr_seq_proc_cpoe
	from	procedimento y,
		prescr_procedimento x,
		prescr_material m,
		prescr_mat_hor c,
		prescr_medica a
	where	y.cd_procedimento = x.cd_procedimento
	and	y.ie_origem_proced = x.ie_origem_proced
	and	x.nr_prescricao = m.nr_prescricao
	and	x.nr_sequencia = m.nr_sequencia_proc
	and	m.nr_prescricao = c.nr_prescricao
	and	m.nr_sequencia = c.nr_seq_material
	and	x.nr_prescricao = a.nr_prescricao
	and	m.nr_prescricao = a.nr_prescricao
	and	c.nr_prescricao = a.nr_prescricao
	and	a.nr_atendimento = nr_atendimento_p
	and ie_palm_p = 'S'
	and	get_ie_exibe_mat_assoc_proced in ('S','X')
	and	exists (SELECT 	1
				from 	material k
				where	m.cd_material		=	k.cd_material
				and		k.ie_tipo_material	in (0,2,3,6,8,9))				
	and	coalesce(x.ie_administrar,'S') = 'S'
	and	coalesce(c.nr_seq_superior::text, '') = ''
	and	((ie_exibir_suspensos_p = 'S') or (coalesce(m.dt_suspensao::text, '') = ''))	
	and	coalesce(c.ie_situacao,'A') = 'A'
	and	c.dt_horario between dt_inicial_horarios_p and dt_final_horarios_p	
	and	((coalesce(c.ie_horario_especial,'N') = 'N') or (c.dt_fim_horario IS NOT NULL AND c.dt_fim_horario::text <> ''))
	and	((ie_exibir_hor_realizados_p = 'S') or (coalesce(c.dt_fim_horario::text, '') = ''))
	and	((ie_exibir_hor_suspensos_p = 'S') or (coalesce(c.dt_suspensao::text, '') = ''))	
	and	Obter_se_horario_liberado(c.dt_lib_horario, c.dt_horario) = 'S'
	and	((get_ie_vigente = 'N') or (clock_timestamp() between a.dt_inicio_prescr and a.dt_validade_prescr + get_minutos_vigente/1440))		
	group by	 
		c.dt_horario,
		a.nr_prescricao,
		x.nr_sequencia,
		c.nr_sequencia,
		c.dt_fim_horario,
		c.dt_primeira_checagem,
		c.dt_suspensao,
		c.ie_dose_especial,
		x.cd_procedimento,
		y.ds_procedimento,
		x.nr_seq_proc_interno,
		x.ie_acm,
		x.ie_se_necessario,
		substr(x.ds_observacao || '  ' || CASE WHEN coalesce(coalesce(x.nr_seq_prescr_mat,x.nr_seq_prescr_sol)::text, '') = '' THEN obter_mat_med_assoc_proc(a.nr_prescricao,x.nr_sequencia,'S')  ELSE '' END  || '  ' || Obter_desc_item_assoc_proc(coalesce(x.nr_seq_prescr_mat,nr_seq_prescr_sol),x.nr_prescricao,CASE WHEN coalesce(nr_seq_prescr_mat::text, '') = '' THEN 'S'  ELSE 'M' END ) ,1,2000),
		x.cd_intervalo,
		x.qt_procedimento,
		x.ie_suspenso,
		c.dt_inicio_horario,
		x.ie_status_atend,
		x.ds_observacao,
		x.nr_seq_proc_cpoe
	
union

	select	a.nr_prescricao,
			CASE WHEN substr(obter_se_associado_dif(a.nr_prescricao,x.nr_sequencia),1,1)='N' THEN x.nr_sequencia  ELSE x.nr_sequencia END ,
			c.nr_sequencia,
			CASE WHEN coalesce(c.dt_primeira_checagem::text, '') = '' THEN substr(obter_status_hor_proced2(c.dt_fim_horario,c.dt_suspensao,c.ie_dose_especial,c.dt_inicio_horario),1,15)  ELSE 'Q' END ,
			x.cd_procedimento,
			y.ds_procedimento,
			x.nr_seq_proc_interno,
			obter_se_acm_sn(x.ie_acm,x.ie_se_necessario) ie_acm_sn,		
			x.cd_intervalo,
			x.qt_procedimento,
			substr(adep_obter_dados_prescr_proc(a.nr_prescricao,x.nr_sequencia,'QIL','S',x.ie_acm,x.ie_se_necessario),1,100) ds_prescricao,
			substr(x.ds_observacao || '  ' || CASE WHEN coalesce(coalesce(x.nr_seq_prescr_mat,x.nr_seq_prescr_sol)::text, '') = '' THEN obter_mat_med_assoc_proc(a.nr_prescricao,x.nr_sequencia,'S')  ELSE '' END  || '  ' || Obter_desc_item_assoc_proc(coalesce(x.nr_seq_prescr_mat,nr_seq_prescr_sol),x.nr_prescricao,CASE WHEN coalesce(nr_seq_prescr_mat::text, '') = '' THEN 'S'  ELSE 'M' END ) ,1,2000) ds_mat_med_assoc,
			CASE WHEN x.ie_suspenso='S' THEN  x.ie_suspenso  ELSE null END  ie_suspenso,
			c.dt_suspensao dt_suspensao,
			c.dt_horario,
			CASE WHEN GetIEObsProced='S' THEN substr(x.ds_observacao,1,2000)  ELSE null END  ds_observacao,
			x.nr_seq_proc_cpoe
	from	procedimento y,
		proc_interno w,
		prescr_procedimento x,
		prescr_material m,
		prescr_mat_hor c,
		prescr_medica a
	where	y.cd_procedimento = x.cd_procedimento
	and	y.ie_origem_proced = x.ie_origem_proced
	and	w.nr_sequencia = x.nr_seq_proc_interno
	and	w.nr_sequencia = x.nr_seq_proc_interno
	and	x.nr_prescricao = m.nr_prescricao
	and	x.nr_sequencia = m.nr_sequencia_proc
	and	m.nr_prescricao = c.nr_prescricao
	and	m.nr_sequencia = c.nr_seq_material
	and	x.nr_prescricao = a.nr_prescricao
	and	m.nr_prescricao = a.nr_prescricao
	and	c.nr_prescricao = a.nr_prescricao
	and	a.nr_atendimento = nr_atendimento_p
	and 	ie_palm_p = 'S'	
	and	get_ie_exibe_mat_assoc_proced in ('S','X')
	and	exists (select 	1 
				from 	material k
				where	m.cd_material		=	k.cd_material
				and		k.ie_tipo_material	in (0,2,3,6,8,9))				
	and	coalesce(x.ie_administrar,'S') = 'S'
	and	coalesce(c.nr_seq_superior::text, '') = ''
	and	((ie_exibir_suspensos_p = 'S') or (coalesce(m.dt_suspensao::text, '') = ''))	
	and	coalesce(c.ie_situacao,'A') = 'A'
	and	c.dt_horario between dt_inicial_horarios_p and dt_final_horarios_p	
	and	((coalesce(c.ie_horario_especial,'N') = 'N') or (c.dt_fim_horario IS NOT NULL AND c.dt_fim_horario::text <> ''))
	and	((ie_exibir_hor_realizados_p = 'S') or (coalesce(c.dt_fim_horario::text, '') = ''))
	and	((ie_exibir_hor_suspensos_p = 'S') or (coalesce(c.dt_suspensao::text, '') = ''))	
	and	Obter_se_horario_liberado(c.dt_lib_horario, c.dt_horario) = 'S'
	and	((get_ie_vigente = 'N') or (clock_timestamp() between a.dt_inicio_prescr and a.dt_validade_prescr + get_minutos_vigente/1440))		
	group by	 
		c.dt_horario,
		a.nr_prescricao,
		x.nr_sequencia,
		c.nr_sequencia,
		c.dt_fim_horario,
		c.dt_primeira_checagem,
		c.dt_suspensao,
		c.ie_dose_especial,
		x.cd_procedimento,
		w.ds_proc_exame,
		y.ds_procedimento,
		x.nr_seq_proc_interno,
		x.ie_acm,
		x.ie_se_necessario,
		substr(x.ds_observacao || '  ' || CASE WHEN coalesce(coalesce(x.nr_seq_prescr_mat,x.nr_seq_prescr_sol)::text, '') = '' THEN obter_mat_med_assoc_proc(a.nr_prescricao,x.nr_sequencia,'S')  ELSE '' END  || '  ' || Obter_desc_item_assoc_proc(coalesce(x.nr_seq_prescr_mat,nr_seq_prescr_sol),x.nr_prescricao,CASE WHEN coalesce(nr_seq_prescr_mat::text, '') = '' THEN 'S'  ELSE 'M' END ) ,1,2000),
		x.cd_intervalo,
		x.qt_procedimento,
		x.ie_suspenso,
		c.dt_inicio_horario,
		x.ie_status_atend,
		x.ds_observacao,
		x.nr_seq_proc_cpoe
	order by
		dt_horario,
		dt_suspensao;
	
BEGIN
	ds_sep_bv_w	:= obter_separador_bv;
	open c01;
	loop
	fetch c01 into	current_setting('adep_gerar_horarios_pck.nr_prescricao_w')::prescr_medica.nr_prescricao%type,
			nr_seq_procedimento_w,
			nr_seq_horario_w,
			ie_status_horario_w,
			cd_procedimento_w,
			ds_procedimento_w,
			nr_seq_proc_interno_w,
			ie_acm_sn_w,		
			cd_intervalo_w,
			qt_procedimento_w,
			ds_prescricao_w,
			ds_mat_med_assoc_w,
			ie_suspenso_w,
			dt_suspensao_w,
			current_setting('adep_gerar_horarios_pck.dt_horario_w')::timestamp,
			ds_observacao_w,
			nr_seq_proc_cpoe_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		nr_horario_w := adep_gerar_horarios_pck.get_posicao_horario(current_setting('adep_gerar_horarios_pck.dt_horario_w')::timestamp);
		if (nr_horario_w IS NOT NULL AND nr_horario_w::text <> '') then
			ds_comando_update_w	:=	' update w_adep_t ' ||
							' set hora' || to_char(nr_horario_w) || ' = :vl_hora, ' ||
							' nr_seq_item = :nr_seq_procedimento, ' ||
							' nr_prescricao = :nr_prescricao ' ||
							' where nm_usuario = :nm_usuario ' ||
							' and ie_tipo_item = :ie_tipo ' ||
							' and nvl(nr_prescricao,nvl(:nr_prescricao,0)) = nvl(:nr_prescricao,0) ' ||
							' and nvl(nr_seq_cpoe,nvl(:nr_seq_cpoe,0)) = nvl(:nr_seq_cpoe,0) ' ||
							' and ((nvl(nr_seq_item,nvl(:nr_seq_item,0)) = nvl(:nr_seq_item,0)) or ' ||
							'      (nvl(nr_seq_item,nvl(:nr_seq_item||:nr_prescricao,0)) = nvl(:nr_seq_item||:nr_prescricao,0))) ' ||
							' and cd_item = :cd_item ' ||
							' and nvl(nr_seq_proc_interno,nvl(:nr_seq_proc_interno,0)) = nvl(:nr_seq_proc_interno,0) ' ||
							' and nvl(cd_intervalo,0) = nvl(:cd_intervalo,0) ' ||
							' and nvl(qt_item,0) = nvl(:qt_item,0) ' ||
							' and nvl(ie_status_item,''N'') = nvl(:ie_suspenso,''N'') ' ||
							' and ((ds_prescricao = :ds_prescricao) or (ds_prescricao is null))  ' ||
							' and nvl(ds_observacao,''XPTO'') = nvl(:ds_observacao,''XPTO'') ';							
			CALL exec_sql_dinamico_bv('ADEP', ds_comando_update_w,	'vl_hora=S' || to_char(nr_seq_horario_w) || 'H' || ie_status_horario_w || ds_sep_bv_w ||
										'nr_prescricao=' || to_char(current_setting('adep_gerar_horarios_pck.nr_prescricao_w')::prescr_medica.nr_prescricao%type) || ds_sep_bv_w ||
										'nr_seq_cpoe=' || to_char(nr_seq_proc_cpoe_w) || ds_sep_bv_w || 
										'nr_seq_procedimento=' || to_char(nr_seq_procedimento_w) || ds_sep_bv_w || 
										'nm_usuario=' || nm_usuario_p || ds_sep_bv_w || 
										'ie_tipo=MP' || ds_sep_bv_w ||
										'nr_seq_item=' || to_char(nr_seq_procedimento_w) || ds_sep_bv_w ||
										'cd_item=' || to_char(cd_procedimento_w) || ds_sep_bv_w ||
										'nr_seq_proc_interno=' || to_char(nr_seq_proc_interno_w) || ds_sep_bv_w ||
						 				'cd_intervalo=' || cd_intervalo_w  || ds_sep_bv_w ||
										'qt_item=' || to_char(qt_procedimento_w) || ds_sep_bv_w ||
										'ie_suspenso=' || ie_suspenso_w || ds_sep_bv_w ||
										'ds_prescricao=' || ds_prescricao_w || ds_sep_bv_w ||
										'ds_observacao=' || ds_observacao_w || ds_sep_bv_w );
			commit;
		end if;
		end;
	end loop;
	close c01;
	CALL Atualizar_adep_controle(nm_usuario_p, nr_atendimento_p, 'P', 'N');
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
