CREATE OR REPLACE PROCEDURE adep_gerar_horarios_pck.adep_sincronizar_solucao ( cd_estabelecimento_p bigint, cd_setor_usuario_p bigint, cd_perfil_p bigint, nm_usuario_p text, nr_atendimento_p bigint, dt_inicial_horarios_p timestamp, dt_final_horarios_p timestamp, dt_validade_limite_p timestamp, ie_exibir_sol_realizadas_p text, ie_exibir_sol_suspensas_p text, ie_regra_inclusao_p text, ie_data_lib_prescr_p text, ie_exibir_suspensos_p text, ie_agrupar_acm_sn_p text, ie_solucoes_continuas_p text, dt_horario_p timestamp, nr_horario_p integer, ie_prescr_setor_p text, ie_formato_solucoes_p text, cd_setor_paciente_p bigint, ie_quimio_p text, ie_palm_p text, ie_param673_p text, dt_referencia_p timestamp, ie_html_p text, ie_clinician_p text) AS $body$
