	c01 CURSOR FOR	
	SELECT	a.nr_prescricao,
			x.nr_seq_solucao,
			CASE WHEN obter_se_solucao_checada(a.nr_prescricao,x.nr_seq_solucao)='N' THEN substr(obter_status_hor_sol_processo(a.nr_prescricao,x.nr_seq_solucao,1),1,15)  ELSE 'Q' END ,
			x.dt_status,
			obter_nr_seq_cpoe_sol(x.nr_prescricao, x.nr_seq_solucao)
	from	prescr_solucao x,
			prescr_medica a
	where	x.nr_prescricao = a.nr_prescricao
	and	obter_se_exibir_rep_adep_setor(cd_setor_paciente_p,a.cd_setor_atendimento,coalesce(a.ie_adep,'S')) = 'S'
	and	a.nr_atendimento = nr_atendimento_p
	and	coalesce(a.ie_hemodialise, 'N') <> 'R'
	and	a.dt_validade_prescr between dt_validade_limite_p and dt_fim_w
	and	obter_se_prescr_lib_adep(coalesce(a.dt_liberacao_medico, x.dt_lib_material), coalesce(a.dt_liberacao, x.dt_lib_material), coalesce(a.dt_liberacao_farmacia, x.dt_lib_farmacia), ie_data_lib_prescr_p, a.ie_lib_farm) = 'S'
	and	coalesce(x.nr_seq_dialise::text, '') = ''
	and	x.ie_status = 'N'
	and	((ie_exibir_sol_realizadas_p = 'S') or (x.ie_status <> 'T'))
	and	((ie_exibir_sol_suspensas_p = 'S') or (coalesce(x.dt_suspensao::text, '') = ''))
	and	((ie_exibir_suspensos_p = 'S') or (coalesce(x.dt_suspensao::text, '') = ''))
	and	((coalesce(a.ie_recem_nato,'N')	= 'N') or (ie_recem_nato_w = 'S'))	
	and	((a.cd_funcao_origem = 2314 and ie_html_p = 'S') or (ie_solucoes_continuas_p = 'S' and ie_html_p = 'N') or (obter_se_sol_continua2(a.nr_prescricao,x.nr_seq_solucao,x.ds_solucao,dt_inicial_horarios_p, dt_final_horarios_p, dt_inicio_sol_w, dt_fim_sol_w,null) = 'N'))
	and	((ie_regra_inclusao_p = 'S') or
		 ((ie_regra_inclusao_p = 'R') and (adep_obter_regra_inclusao(	'SOL',
																		cd_estabelecimento_p, 
																		cd_setor_usuario_p, 
																		cd_perfil_p, 
																		null, 
																		null, 
																		null, 
																		null,
																		a.cd_Setor_atendimento,
																		null,
																		null,			-- nr_prescricao_p. Passei nulo porque criaram o param na adep_obter_regra_inclusao como default null, e nao haviam passado nada
																		null) = 'S'))) -- nr_seq_exame_p
	and	x.dt_status between dt_horario_inicio_sinc_w and dt_horario_fim_sinc_w
	and	((ie_prescr_setor_p = 'N') or (ie_prescr_setor_p = 'S' AND a.cd_setor_atendimento = cd_setor_paciente_p))
	and	((get_ie_vigente = 'N') or (clock_timestamp() between a.dt_inicio_prescr and a.dt_validade_prescr + get_minutos_vigente/1440))
	and	((ie_quimio_p = 'S') or (coalesce(a.nr_seq_atend::text, '') = ''))
	group by
		x.dt_status,
		a.nr_prescricao,
		x.nr_seq_solucao,
		obter_nr_seq_cpoe_sol(x.nr_prescricao, x.nr_seq_solucao)
	order by x.dt_status;

	c02 CURSOR FOR
	SELECT	a.nr_prescricao,
		x.nr_seq_solucao,
