	and	((a.cd_funcao_origem = 2314 and ie_html_p = 'S') or (ie_solucoes_continuas_p = 'S' and ie_html_p = 'N') or (obter_se_sol_continua2(a.nr_prescricao,x.nr_seq_solucao,x.ds_solucao,dt_inicial_horarios_p, dt_final_horarios_p, dt_inicio_sol_w, dt_fim_sol_w, null) = 'N'))
	--and	((ie_solucoes_continuas_p = 'S') or
		-- (obter_se_sol_continua2(a.nr_prescricao,x.nr_seq_solucao,x.ds_solucao,dt_inicial_horarios_p, dt_final_horarios_p, dt_inicio_sol_w,dt_fim_sol_w) = 'N'))
	and	((ie_regra_inclusao_p = 'S') or
		 ((ie_regra_inclusao_p = 'R') and (adep_obter_regra_inclusao(	'SOL',
																		cd_estabelecimento_p, 
																		cd_setor_usuario_p, 
																		cd_perfil_p, 
																		null, 
																		null, 
																		null, 
																		null,
																		a.cd_Setor_atendimento,
																		null,
																		null,			-- nr_prescricao_p. Passei nulo porque criaram o param na adep_obter_regra_inclusao como default null, e nao haviam passado nada
																		null) = 'S'))) -- nr_seq_exame_p
	and	((ie_prescr_setor_p = 'N') or 
		 (ie_prescr_setor_p = 'S' AND a.cd_setor_atendimento = cd_setor_paciente_p))
	and	((get_ie_vigente = 'N') or (clock_timestamp() between a.dt_inicio_prescr and a.dt_validade_prescr + get_minutos_vigente/1440))	 
	and	((ie_quimio_p		= 'S') or (coalesce(a.nr_seq_atend::text, '') = ''))
	and	(((coalesce(ie_palm_p,'N') = 'S') and (coalesce(c.dt_inicio_horario::text, '') = '')) or (coalesce(ie_palm_p,'N') = 'N') or (ie_clinician_p = 'S'))	
	group by
		a.nr_prescricao,
		x.nr_seq_solucao,
		CASE WHEN coalesce(c.dt_primeira_checagem::text, '') = '' THEN 			CASE WHEN coalesce(c.dt_recusa::text, '') = '' THEN 				CASE WHEN get_ie_usa_status_gedipa='S' THEN  				substr(obter_status_hor_sol_adep_esp(c.dt_inicio_horario,c.dt_fim_horario,c.dt_suspensao,c.dt_interrupcao,c.ie_dose_especial,c.nr_seq_processo,c.nr_seq_area_prep,a.nr_prescricao,x.nr_seq_solucao,x.ie_solucao_especial,ie_param673_p,c.nr_sequencia),1,15)  ELSE substr(obter_status_hor_sol_adep_esp(c.dt_inicio_horario,c.dt_fim_horario,c.dt_suspensao,c.dt_interrupcao,c.ie_dose_especial,null,null,a.nr_prescricao,x.nr_seq_solucao,x.ie_solucao_especial,ie_param673_p,c.nr_sequencia),1,15) END   ELSE substr(obter_status_recusa_pac(c.dt_fim_horario,c.dt_suspensao),1,15) END   ELSE 'Q' END ,
		c.nr_etapa_sol,
		c.dt_prev_fim_horario,
		c.dt_horario,
		CASE WHEN ie_palm_p='S' THEN c.nr_sequencia  ELSE null END ,
		substr(obter_desc_vol_etapa_adep2(CASE WHEN adep_gerar_horarios_pck.get_ie_agrupa_solucao(ie_html_p)='S' THEN  obter_prescricao_solucao_cpoe(obter_nr_seq_cpoe_sol(x.nr_prescricao, x.nr_seq_solucao))  ELSE a.nr_prescricao END , CASE WHEN adep_gerar_horarios_pck.get_ie_agrupa_solucao(ie_html_p)='S' THEN  get_nr_seq_solucao_agrupada(obter_nr_seq_cpoe_sol(x.nr_prescricao, x.nr_seq_solucao))  ELSE x.nr_seq_solucao END ,x.ie_acm,x.ie_se_necessario, adep_gerar_horarios_pck.get_ie_agrupa_solucao(ie_html_p)),1,240),
		substr(CASE WHEN GetJuntaCompSolucao='S' THEN obter_Concat_comp_sol(a.nr_prescricao,x.nr_seq_solucao)  ELSE coalesce(trim(both x.ds_solucao),obter_prim_comp_sol(a.nr_prescricao,x.nr_seq_solucao)) END ,1,240),
		obter_nr_seq_cpoe_sol(x.nr_prescricao, x.nr_seq_solucao),
		substr(CASE WHEN coalesce(obter_nr_seq_cpoe_sol(x.nr_prescricao, x.nr_seq_solucao)::text, '') = '' THEN  null  ELSE obter_se_veloc_variavel(x.nr_prescricao, x.nr_seq_solucao) END  || chr(13) || get_cpoe_notes(obter_nr_seq_cpoe_sol(a.nr_prescricao, x.nr_seq_solucao)) || adep_gerar_horarios_pck.obter_valor_dose_ataque(obter_ds_solucao_dose_ataque(obter_nr_seq_cpoe_sol(x.nr_prescricao, x.nr_seq_solucao), dt_referencia_p)) || CASE WHEN coalesce(obter_nr_seq_cpoe_sol(a.nr_prescricao, x.nr_seq_solucao)::text, '') = '' THEN  obter_componentes_solucao(a.nr_prescricao,x.nr_seq_solucao,'S',nm_usuario_p,cd_estabelecimento_p)  ELSE get_solution_add_info(obter_nr_seq_cpoe_sol(a.nr_prescricao, x.nr_seq_solucao)) END  || chr(13) || obter_desc_expressao(301661) || ':' || substr(obter_Desc_via(x.ie_via_aplicacao),1,30) || chr(13) || CASE WHEN coalesce(obter_nr_seq_cpoe_sol(x.nr_prescricao, x.nr_seq_solucao)::text, '') = '' THEN  CASE WHEN coalesce(x.DS_ORIENTACAO::text, '') = '' THEN ''  ELSE wheb_mensagem_pck.get_texto(857391,'ds_orientacao='|| x.DS_ORIENTACAO) END   ELSE null END  || adep_gerar_horarios_pck.obter_ds_justificativa(obter_justificativa_item(a.nr_prescricao, x.nr_seq_solucao, 'SOL')),1,2000),
		substr(CASE WHEN adep_gerar_horarios_pck.get_ie_agrupa_solucao(ie_html_p)='S' THEN  obter_status_solucao_cpoe(obter_nr_seq_cpoe_sol(x.nr_prescricao, x.nr_seq_solucao))  ELSE obter_status_solucao_prescr(1,a.nr_prescricao,x.nr_seq_solucao) END ,1,3)
	order by c.dt_horario;
	
BEGIN
	ds_sep_bv_w	:= obter_separador_bv;
	dt_inicial_w	:= dt_horario_p	- 2;
	dt_final_w	:= dt_horario_p + 2;
	SELECT * FROM adep_gerar_horarios_pck.getdtrefsol(dt_validade_limite_p, dt_inicio_sol_w, dt_fim_sol_w) INTO STRICT dt_inicio_sol_w, dt_fim_sol_w;	
	if (ie_formato_solucoes_p = 'I') then
		begin
		open c01;
		loop
		fetch c01 into	current_setting('adep_gerar_horarios_pck.nr_prescricao_w')::prescr_medica.nr_prescricao%type,
				nr_seq_solucao_w,
				ie_status_horario_w,
				current_setting('adep_gerar_horarios_pck.dt_horario_w')::timestamp,
				nr_seq_sol_cpoe_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
			begin
			if (ie_status_horario_w not in ('A', 'S')) and (coalesce(get_qt_minutos_atraso,0) > 0) then
				select 	obter_se_soluc_atraso_adep(current_setting('adep_gerar_horarios_pck.nr_prescricao_w')::prescr_medica.nr_prescricao%type, nr_seq_solucao_w, ie_status_horario_w, get_qt_minutos_atraso)
				into STRICT	ie_medic_atrasado_w
				;				
			end if;			
			nr_horario_w := adep_gerar_horarios_pck.get_posicao_horario( current_setting('adep_gerar_horarios_pck.dt_horario_w')::timestamp );

			ds_comando_update_w	:=	' update w_adep_t ' ||
				' set nr_prescricoes = adep_juntar_prescricao(nr_prescricoes,:nr_prescricao), ' ||
				' ie_atrasado = :ie_atrasado ' ||
				' where nm_usuario = :nm_usuario ' ||
				' and ie_tipo_item = :ie_tipo ' ||
				' and nvl(nr_prescricao,nvl(:nr_prescricao,0)) = nvl(:nr_prescricao,0) ' ||
				' and nvl(nr_seq_cpoe,nvl(:nr_seq_cpoe,0)) = nvl(:nr_seq_cpoe,0) ' ||
				' and nvl(nr_seq_item,nvl(:nr_seq_item,0)) = nvl(:nr_seq_item,0) ';
			CALL exec_sql_dinamico_bv('ADEP', ds_comando_update_w,'ie_atrasado=' || ie_medic_atrasado_w || ds_sep_bv_w || 	
				'nr_prescricao=' || to_char(current_setting('adep_gerar_horarios_pck.nr_prescricao_w')::prescr_medica.nr_prescricao%type) || ds_sep_bv_w ||
				'nr_seq_cpoe=' || to_char(nr_seq_sol_cpoe_w) || ds_sep_bv_w || 
				'nm_usuario=' || nm_usuario_p || ds_sep_bv_w || 
				'ie_tipo=SOL' || ds_sep_bv_w ||
				'nr_seq_item=' || to_char(nr_seq_solucao_w) || ds_sep_bv_w );
			commit;
			
			if (nr_horario_w IS NOT NULL AND nr_horario_w::text <> '') then
				ds_comando_update_w	:=	' update w_adep_t ' ||
								' set hora' || to_char(nr_horario_w) || ' = :vl_hora, ' ||
								' nr_prescricoes = adep_juntar_prescricao(nr_prescricoes,:nr_prescricao), ' ||
								' ds_horarios = adep_atualizar_inf_horarios(ds_horarios,:nr_horario,:vl_hora), ' ||
								' ie_atrasado = :ie_atrasado ' ||
								' where nm_usuario = :nm_usuario ' ||
								' and ie_tipo_item = :ie_tipo ' ||
								' and nvl(nr_prescricao,nvl(:nr_prescricao,0)) = nvl(:nr_prescricao,0) ' ||
								' and nvl(nr_seq_cpoe,nvl(:nr_seq_cpoe,0)) = nvl(:nr_seq_cpoe,0) ' ||
								' and nvl(nr_seq_item,nvl(:nr_seq_item,0)) = nvl(:nr_seq_item,0) ';
				CALL exec_sql_dinamico_bv('ADEP', ds_comando_update_w,	'vl_hora=S' || to_char(nr_seq_horario_w) || 'H' || ie_status_horario_w || ds_sep_bv_w ||
											'ie_atrasado=' || ie_medic_atrasado_w || ds_sep_bv_w || 	
											'nr_prescricao=' || to_char(current_setting('adep_gerar_horarios_pck.nr_prescricao_w')::prescr_medica.nr_prescricao%type) || ds_sep_bv_w ||
											'nr_seq_cpoe=' || to_char(nr_seq_sol_cpoe_w) || ds_sep_bv_w || 
											'nr_horario=' || to_char(nr_horario_w) || ds_sep_bv_w || 
											'nm_usuario=' || nm_usuario_p || ds_sep_bv_w || 
											'ie_tipo=SOL' || ds_sep_bv_w ||
											'nr_seq_item=' || to_char(nr_seq_solucao_w) || ds_sep_bv_w );
				commit;
			end if;
			end;
		end loop;
		close c01;
		end;
	elsif (ie_formato_solucoes_p = 'H') then
		begin

		if (ie_html_p = 'S') then
			select	CASE WHEN adep_gerar_horarios_pck.get_ie_agrupa_solucao(ie_html_p)='S' THEN  'N'  ELSE 'S' END
			into STRICT	ie_solucoes_continuas_ww
			;
		else
			ie_solucoes_continuas_ww := ie_solucoes_continuas_p;
		end if;

		open c02;
		loop
		fetch c02 into	current_setting('adep_gerar_horarios_pck.nr_prescricao_w')::prescr_medica.nr_prescricao%type,
				nr_seq_solucao_w,
				ie_status_horario_w,
				nr_etapa_sol_w,
				dt_etapa_prev_w,
				current_setting('adep_gerar_horarios_pck.dt_horario_w')::timestamp,
				nr_seq_horario_w,
				ds_item_w,
				ds_solucao_w,
				nr_seq_sol_cpoe_w,
				ds_componentes_w,
				ie_status_solucao_w;
		EXIT WHEN NOT FOUND; /* apply on c02 */
			begin
			select 	count(*)
			into STRICT	qt_sol_igual_prescr_w
			from 	prescr_solucao x,
				prescr_medica a
			where   x.nr_prescricao = a.nr_prescricao
			and     x.nr_prescricao = current_setting('adep_gerar_horarios_pck.nr_prescricao_w')::prescr_medica.nr_prescricao%type
			and (SELECT coalesce(max(b.nr_prescricao),0)
				 from w_adep_t b 
				 where b.nr_prescricao = x.nr_prescricao 
				 and b.nm_usuario = nm_usuario_p 
				 and b.ie_tipo_item = 'SOL' 
				 and b.ds_componentes = ds_componentes_w
				 and b.nr_seq_item = nr_seq_solucao_w) = 0
			and  substr(adep_gerar_horarios_pck.obter_valor_dose_ataque(obter_ds_solucao_dose_ataque(obter_nr_seq_cpoe_sol(x.nr_prescricao, x.nr_seq_solucao), dt_referencia_p)) || obter_componentes_solucao(a.nr_prescricao,x.nr_seq_solucao,'S',nm_usuario_p,cd_estabelecimento_p) || CASE WHEN coalesce(obter_nr_seq_cpoe_sol(x.nr_prescricao, x.nr_seq_solucao)::text, '') = '' THEN  CASE WHEN coalesce(x.DS_ORIENTACAO::text, '') = '' THEN ''  ELSE wheb_mensagem_pck.get_texto(857391,'ds_orientacao='|| x.DS_ORIENTACAO) END   ELSE wheb_mensagem_pck.get_texto(857391,'ds_orientacao='|| get_dados_dil_cpoe_js(a.nr_prescricao,x.nr_seq_solucao)) END ,1,2000) = ds_componentes_w;
			dt_suspensao_cpoe_w := cpoe_obter_dt_suspensao(nr_seq_sol_cpoe_w, 'M');
			if	dt_suspensao_cpoe_w <= clock_timestamp() then
				ds_item_w := wheb_mensagem_pck.get_texto(820376) || ' ' || substr(ds_item_w,1,240);
			elsif (dt_suspensao_cpoe_w > clock_timestamp()) then
				ds_item_w := '(' || wheb_mensagem_pck.get_texto(1061446, 'DT_SUSPENSAO=' || TO_CHAR(dt_suspensao_cpoe_w, pkg_date_formaters.localize_mask('short', pkg_date_formaters.getUserLanguageTag(wheb_usuario_pck.get_cd_estabelecimento, wheb_usuario_pck.get_nm_usuario)))) || ') ' || substr(ds_item_w,1,240);
			end if;
			if (ie_status_horario_w not in ('A', 'S')) and (coalesce(get_qt_minutos_atraso,0) > 0) then
				select 	obter_se_soluc_atraso_adep(current_setting('adep_gerar_horarios_pck.nr_prescricao_w')::prescr_medica.nr_prescricao%type, nr_seq_solucao_w, ie_status_horario_w, get_qt_minutos_atraso)
				into STRICT	ie_medic_atrasado_w
				;				
			end if;
			nr_horario_w := adep_gerar_horarios_pck.get_posicao_horario( current_setting('adep_gerar_horarios_pck.dt_horario_w')::timestamp );

			if (dt_etapa_prev_w IS NOT NULL AND dt_etapa_prev_w::text <> '') then
				ds_comando_update_w	:=	' update w_adep_t ' ||
					' set nr_prescricoes = adep_juntar_prescricao(nr_prescricoes,:nr_prescricao), ' ||
					' dt_prev_term =  :dt_prev_term, ' ||
					' ie_atrasado = :ie_atrasado ' ||
					' where nm_usuario = :nm_usuario ' ||
					' and ie_tipo_item = :ie_tipo ' ||
					' and ds_componentes = :ds_componentes ' ||
					' and ((nvl(nr_prescricao,nvl(:nr_prescricao,0)) = nvl(:nr_prescricao,0)) or ( :qt_sol_igual_prescr > 0 and (ds_item = :ds_solucao)) or ((ds_item = :ds_solucao) and (ds_prescricao = :ds_item) and (''N'' = :ie_solucoes_continuas))) ' ||
					' and ((nvl(nr_seq_item,nvl(:nr_seq_item,0)) = nvl(:nr_seq_item,0)) or ( :qt_sol_igual_prescr > 0 and (ds_item = :ds_solucao)) or ((ds_item = :ds_solucao) and (ds_prescricao = :ds_item) and (''N'' = :ie_solucoes_continuas)))' ||
					' and ie_status_item = :ie_status_sol ';
				CALL exec_sql_dinamico_bv('ADEP', ds_comando_update_w, 'ie_atrasado=' || ie_medic_atrasado_w || ds_sep_bv_w ||
												'nr_prescricao=' || to_char(current_setting('adep_gerar_horarios_pck.nr_prescricao_w')::prescr_medica.nr_prescricao%type) || ds_sep_bv_w ||
												'dt_prev_term=' || to_char(dt_etapa_prev_w, 'dd/mm/yyyy hh24:mi:ss') || ds_sep_bv_w ||
												'nm_usuario=' || nm_usuario_p || ds_sep_bv_w ||
												'ie_tipo=SOL' || ds_sep_bv_w ||
												'ds_item=' || ds_item_w || ds_sep_bv_w || 
												'ds_solucao=' || ds_solucao_w || ds_sep_bv_w ||												
												'ds_componentes=' || ds_componentes_w || ds_sep_bv_w ||
												'ie_solucoes_continuas=' || ie_solucoes_continuas_ww || ds_sep_bv_w || 
												'nr_seq_item=' || to_char(nr_seq_solucao_w) || ds_sep_bv_w ||
												'qt_sol_igual_prescr=' || to_char(qt_sol_igual_prescr_w) || ds_sep_bv_w ||
												'ie_status_sol=' || ie_status_solucao_w);
				commit;
			else
				ds_comando_update_w	:=	' update w_adep_t ' ||
					' set nr_prescricoes = adep_juntar_prescricao(nr_prescricoes,:nr_prescricao), ' ||
					' dt_prev_term =  null, ' ||
					' ie_atrasado = :ie_atrasado ' ||
					' where nm_usuario = :nm_usuario ' ||
					' and ie_tipo_item = :ie_tipo ' ||
					' and ds_componentes = :ds_componentes ' ||
					' and ((nvl(nr_prescricao,nvl(:nr_prescricao,0)) = nvl(:nr_prescricao,0)) or ( :qt_sol_igual_prescr > 0 and (ds_item = :ds_solucao)) or  ((ds_item = :ds_solucao) and (ds_prescricao = :ds_item) and (''N'' = :ie_solucoes_continuas))) ' ||
					' and ((nvl(nr_seq_item,nvl(:nr_seq_item,0)) = nvl(:nr_seq_item,0)) or ( :qt_sol_igual_prescr > 0 and (ds_item = :ds_solucao)) or ((ds_item = :ds_solucao) and (ds_prescricao = :ds_item) and (''N'' = :ie_solucoes_continuas)))' ||
					' and ie_status_item = :ie_status_sol ';
				CALL exec_sql_dinamico_bv('ADEP', ds_comando_update_w, 'ie_atrasado=' || ie_medic_atrasado_w || ds_sep_bv_w ||
					'nr_prescricao=' || to_char(current_setting('adep_gerar_horarios_pck.nr_prescricao_w')::prescr_medica.nr_prescricao%type) || ds_sep_bv_w ||
					'nm_usuario=' || nm_usuario_p || ds_sep_bv_w ||
					'ie_tipo=SOL' || ds_sep_bv_w ||
					'ds_item=' || ds_item_w || ds_sep_bv_w ||
					'ds_solucao=' || ds_solucao_w || ds_sep_bv_w ||
					'ds_componentes=' || ds_componentes_w || ds_sep_bv_w ||
					'ie_solucoes_continuas=' || ie_solucoes_continuas_ww || ds_sep_bv_w ||
					'nr_seq_item=' || to_char(nr_seq_solucao_w) || ds_sep_bv_w ||
					'qt_sol_igual_prescr=' || to_char(qt_sol_igual_prescr_w) || ds_sep_bv_w ||
					'ie_status_sol=' || ie_status_solucao_w);
				commit;
			end if;

			if (nr_horario_w IS NOT NULL AND nr_horario_w::text <> '') then
				if (dt_etapa_prev_w IS NOT NULL AND dt_etapa_prev_w::text <> '') then
					ds_comando_update_w	:=	' update w_adep_t ' ||
									' set hora' || to_char(nr_horario_w) || ' = :vl_hora, ' ||
									' nr_prescricoes = adep_juntar_prescricao(nr_prescricoes,:nr_prescricao), ' ||
									' ds_horarios = adep_atualizar_inf_horarios(ds_horarios,:nr_horario,:vl_hora), ' ||
									' dt_prev_term =  :dt_prev_term, ' ||
									' ie_atrasado = :ie_atrasado ' ||
									' where nm_usuario = :nm_usuario ' ||
									' and ie_tipo_item = :ie_tipo ' ||
									--' and nvl(nr_seq_cpoe,nvl(:nr_seq_cpoe,0)) = nvl(:nr_seq_cpoe,0) ' ||
									' and ds_componentes = :ds_componentes ' ||
									' and ((nvl(nr_prescricao,nvl(:nr_prescricao,0)) = nvl(:nr_prescricao,0)) or ( :qt_sol_igual_prescr > 0 and (ds_item = :ds_solucao)) or ((ds_item = :ds_solucao) and (ds_prescricao = :ds_item) and (''N'' = :ie_solucoes_continuas))) ' ||
									--' and ((nvl(nr_seq_item,nvl(:nr_seq_item,0)) = nvl(:nr_seq_item,0)) or ((ds_item = :ds_solucao) and (ds_prescricao = :ds_item) and (''N'' = :ie_solucoes_continuas))) ';--aqui
									'and ((nvl(nr_seq_item,nvl(:nr_seq_item,0)) = nvl(:nr_seq_item,0)) or ( :qt_sol_igual_prescr > 0 and (ds_item = :ds_solucao)) or ((ds_item = :ds_solucao) and (ds_prescricao = :ds_item) and (''N'' = :ie_solucoes_continuas)))' ||
									' and ie_status_item = :ie_status_sol ';
					
					if (ie_clinician_p = 'S') then
					
						CALL exec_sql_dinamico_bv('ADEP', ds_comando_update_w,	'vl_hora=S' || to_char(nr_seq_horario_w) || 'H' || ie_status_horario_w || ds_sep_bv_w ||
												'ie_atrasado=' || ie_medic_atrasado_w || ds_sep_bv_w || 	
												'nr_prescricao=' || to_char(current_setting('adep_gerar_horarios_pck.nr_prescricao_w')::prescr_medica.nr_prescricao%type) || ds_sep_bv_w ||
												--'nr_seq_cpoe=' || to_char(nr_seq_sol_cpoe_w) || ds_sep_bv_w || 												
												'nr_horario=' || to_char(nr_horario_w) || ds_sep_bv_w || 
												'dt_prev_term=' || to_char(dt_etapa_prev_w, 'dd/mm/yyyy hh24:mi:ss') || ds_sep_bv_w ||
												'nm_usuario=' || nm_usuario_p || ds_sep_bv_w || 
												'ie_tipo=SOL' || ds_sep_bv_w ||
												'ds_item=' || ds_item_w || ds_sep_bv_w || 
												'ds_solucao=' || ds_solucao_w || ds_sep_bv_w ||												
												'ds_componentes=' || ds_componentes_w || ds_sep_bv_w ||
												'ie_solucoes_continuas=' || ie_solucoes_continuas_ww || ds_sep_bv_w || 
												'nr_seq_item=' || to_char(nr_seq_solucao_w) || ds_sep_bv_w ||
												'qt_sol_igual_prescr=' || to_char(qt_sol_igual_prescr_w) || ds_sep_bv_w ||
												'ie_status_sol=' || ie_status_solucao_w);
				
					else
					
					
						CALL exec_sql_dinamico_bv('ADEP', ds_comando_update_w,	'vl_hora=S' || to_char(nr_seq_horario_w) || to_char(nr_etapa_sol_w) || 'H' || ie_status_horario_w || ds_sep_bv_w ||
												'ie_atrasado=' || ie_medic_atrasado_w || ds_sep_bv_w || 	
												'nr_prescricao=' || to_char(current_setting('adep_gerar_horarios_pck.nr_prescricao_w')::prescr_medica.nr_prescricao%type) || ds_sep_bv_w ||
												--'nr_seq_cpoe=' || to_char(nr_seq_sol_cpoe_w) || ds_sep_bv_w || 												
												'nr_horario=' || to_char(nr_horario_w) || ds_sep_bv_w || 
												'dt_prev_term=' || to_char(dt_etapa_prev_w, 'dd/mm/yyyy hh24:mi:ss') || ds_sep_bv_w ||
												'nm_usuario=' || nm_usuario_p || ds_sep_bv_w || 
												'ie_tipo=SOL' || ds_sep_bv_w ||
												'ds_item=' || ds_item_w || ds_sep_bv_w || 
												'ds_solucao=' || ds_solucao_w || ds_sep_bv_w ||												
												'ds_componentes=' || ds_componentes_w || ds_sep_bv_w ||
												'ie_solucoes_continuas=' || ie_solucoes_continuas_ww || ds_sep_bv_w || 
												'nr_seq_item=' || to_char(nr_seq_solucao_w) || ds_sep_bv_w ||
												'qt_sol_igual_prescr=' || to_char(qt_sol_igual_prescr_w) || ds_sep_bv_w ||
												'ie_status_sol=' || ie_status_solucao_w);
					end if;
					commit;
				else
					ds_comando_update_w	:=	' update w_adep_t ' ||
											' set hora' || to_char(nr_horario_w) || ' = :vl_hora, ' ||
											' nr_prescricoes = adep_juntar_prescricao(nr_prescricoes,:nr_prescricao), ' ||
											' ds_horarios = adep_atualizar_inf_horarios(ds_horarios,:nr_horario,:vl_hora), ' ||
											' dt_prev_term =  null, ' ||
											' ie_atrasado = :ie_atrasado ' ||
											' where nm_usuario = :nm_usuario ' ||
											' and ie_tipo_item = :ie_tipo ' ||
											--' and nvl(nr_seq_cpoe,nvl(:nr_seq_cpoe,0)) = nvl(:nr_seq_cpoe,0) ' ||
											' and ds_componentes = :ds_componentes ' ||
											' and ((nvl(nr_prescricao,nvl(:nr_prescricao,0)) = nvl(:nr_prescricao,0)) or ( :qt_sol_igual_prescr > 0 and (ds_item = :ds_solucao)) or  ((ds_item = :ds_solucao) and (ds_prescricao = :ds_item) and (''N'' = :ie_solucoes_continuas))) ' ||
											--' and ((nvl(nr_seq_item,nvl(:nr_seq_item,0)) = nvl(:nr_seq_item,0)) or ((ds_item = :ds_solucao) and (ds_prescricao = :ds_item) and (''N'' = :ie_solucoes_continuas))) ';
											'and ((nvl(nr_seq_item,nvl(:nr_seq_item,0)) = nvl(:nr_seq_item,0)) or ( :qt_sol_igual_prescr > 0 and (ds_item = :ds_solucao)) or ((ds_item = :ds_solucao) and (ds_prescricao = :ds_item) and (''N'' = :ie_solucoes_continuas)))' ||
											' and ie_status_item = :ie_status_sol ';


					if (ie_clinician_p = 'S') then
							CALL exec_sql_dinamico_bv('ADEP', ds_comando_update_w,	'vl_hora=S' || to_char(nr_seq_horario_w) || 'H' || ie_status_horario_w || ds_sep_bv_w ||
												'ie_atrasado=' || ie_medic_atrasado_w || ds_sep_bv_w || 	
												'nr_prescricao=' || to_char(current_setting('adep_gerar_horarios_pck.nr_prescricao_w')::prescr_medica.nr_prescricao%type) || ds_sep_bv_w || 	
												--'nr_seq_cpoe=' || to_char(nr_seq_sol_cpoe_w) || ds_sep_bv_w || 		
												'nr_horario=' || to_char(nr_horario_w) || ds_sep_bv_w ||
												'nm_usuario=' || nm_usuario_p || ds_sep_bv_w || 
												'ie_tipo=SOL' || ds_sep_bv_w ||
												'ds_item=' || ds_item_w || ds_sep_bv_w || 
												'ds_solucao=' || ds_solucao_w || ds_sep_bv_w ||
												'ds_componentes=' || ds_componentes_w || ds_sep_bv_w ||
												'ie_solucoes_continuas=' || ie_solucoes_continuas_ww || ds_sep_bv_w || 
												'nr_seq_item=' || to_char(nr_seq_solucao_w) || ds_sep_bv_w ||
												'qt_sol_igual_prescr=' || to_char(qt_sol_igual_prescr_w) || ds_sep_bv_w ||
												'ie_status_sol=' || ie_status_solucao_w);
					else
							CALL exec_sql_dinamico_bv('ADEP', ds_comando_update_w,	'vl_hora=S' || to_char(nr_seq_horario_w) || to_char(nr_etapa_sol_w) || 'H' || ie_status_horario_w || ds_sep_bv_w ||
												'ie_atrasado=' || ie_medic_atrasado_w || ds_sep_bv_w || 	
												'nr_prescricao=' || to_char(current_setting('adep_gerar_horarios_pck.nr_prescricao_w')::prescr_medica.nr_prescricao%type) || ds_sep_bv_w || 	
												--'nr_seq_cpoe=' || to_char(nr_seq_sol_cpoe_w) || ds_sep_bv_w || 		
												'nr_horario=' || to_char(nr_horario_w) || ds_sep_bv_w ||
												'nm_usuario=' || nm_usuario_p || ds_sep_bv_w || 
												'ie_tipo=SOL' || ds_sep_bv_w ||
												'ds_item=' || ds_item_w || ds_sep_bv_w || 
												'ds_solucao=' || ds_solucao_w || ds_sep_bv_w ||
												'ds_componentes=' || ds_componentes_w || ds_sep_bv_w ||
												'ie_solucoes_continuas=' || ie_solucoes_continuas_ww || ds_sep_bv_w || 
												'nr_seq_item=' || to_char(nr_seq_solucao_w) || ds_sep_bv_w ||
												'qt_sol_igual_prescr=' || to_char(qt_sol_igual_prescr_w) || ds_sep_bv_w ||
												'ie_status_sol=' || ie_status_solucao_w);
					end if;
					commit;
				end if;
			end if;	
			end;
		end loop;
		close c02;	
		end;
	end if;
	CALL Atualizar_adep_controle(nm_usuario_p, nr_atendimento_p, 'SOL', 'N');
	END;

$body$
LANGUAGE PLPGSQL
