CREATE OR REPLACE PROCEDURE adep_gerar_horarios_pck.adep_sincronizar_supl_oral ( cd_estabelecimento_p bigint, cd_setor_usuario_p bigint, cd_perfil_p bigint, nm_usuario_p text, nr_atendimento_p bigint, dt_inicial_horarios_p timestamp, dt_final_horarios_p timestamp, dt_validade_limite_p timestamp, ie_exibir_hor_realizados_p text, ie_exibir_hor_suspensos_p text, ie_regra_inclusao_p text, ie_data_lib_prescr_p text, ie_exibir_suspensos_p text, ie_agrupar_acm_sn_p text, dt_horario_p timestamp, nr_horario_p integer, ie_exibe_sem_lib_farm_p text, ie_prescr_setor_p text, cd_setor_paciente_p bigint) AS $body$
DECLARE

	ds_sep_bv_w		varchar(50);
	nr_seq_material_w	integer;
	nr_seq_horario_w	bigint;
	ie_status_horario_w	varchar(1);
	cd_material_w		integer;
	ds_material_w		varchar(255);
	ie_acm_sn_w		varchar(1);
	cd_intervalo_w		varchar(7);
	qt_dose_w		double precision;
	ds_prescricao_w		varchar(240);
	ie_status_w		varchar(1);
	ie_ciente_w		varchar(1);
	ds_comando_update_w	varchar(4000);
	current_setting('adep_gerar_horarios_pck.dt_horario_w')::timestamp	timestamp;
	nr_horario_w	integer;
	ie_utiliza_serv_w varchar(1) := adep_gerar_horarios_pck.get_usa_servico_dietas();
	ie_recem_nato_w		varchar(1) := adep_gerar_horarios_pck.get_ie_recem_nato();	
	nr_seq_dieta_cpoe_w	prescr_material.nr_seq_dieta_cpoe%type;
	ds_observacao_w		varchar(2000);
	c01 CURSOR FOR
	SELECT	a.nr_prescricao,
			c.nr_seq_material,
			c.nr_sequencia,
			CASE WHEN CASE WHEN  coalesce(c.dt_primeira_checagem::text, '') = '' THEN 					CASE WHEN coalesce(c.dt_bloqueio::text, '') = '' THEN 					CASE WHEN ie_utiliza_serv_w='S' THEN 					CASE WHEN coalesce(adep_obter_data_lib_nut_serv(c.nr_sequencia)::text, '') = '' THEN  							'Y'  ELSE substr(obter_status_hor_medic(c.dt_fim_horario,c.dt_suspensao,c.ie_dose_especial,c.nr_seq_processo,c.nr_seq_area_prep),1,15) END   ELSE substr(obter_status_hor_medic(c.dt_fim_horario,c.dt_suspensao,c.ie_dose_especial,c.nr_seq_processo,c.nr_seq_area_prep),1,15) END   ELSE 'B' END   ELSE 'Q' END ='N' THEN CASE WHEN coalesce(coalesce(c.ie_ciente,x.ie_ciente),'N')='S' THEN 'W'  ELSE 'N' END   ELSE CASE WHEN  coalesce(c.dt_primeira_checagem::text, '') = '' THEN 																																											CASE WHEN coalesce(c.dt_bloqueio::text, '') = '' THEN 																																											CASE WHEN ie_utiliza_serv_w='S' THEN 																																											CASE WHEN coalesce(adep_obter_data_lib_nut_serv(c.nr_sequencia)::text, '') = '' THEN  																																													'Y'  ELSE substr(obter_status_hor_medic(c.dt_fim_horario,c.dt_suspensao,c.ie_dose_especial,c.nr_seq_processo,c.nr_seq_area_prep),1,15) END   ELSE substr(obter_status_hor_medic(c.dt_fim_horario,c.dt_suspensao,c.ie_dose_especial,c.nr_seq_processo,c.nr_seq_area_prep),1,15) END   ELSE 'B' END   ELSE 'Q' END  END ,
			c.cd_material,
			obter_se_acm_sn(x.ie_acm,x.ie_se_necessario) ie_acm_sn,
			x.cd_intervalo,
			x.qt_dose,
			substr(adep_obter_um_dosagem_prescr(a.nr_prescricao,c.nr_seq_material,x.ie_acm,x.ie_se_necessario),1,100) ds_prescricao,
			coalesce(x.ie_suspenso,'N') ie_suspenso,	
			c.dt_horario,
			coalesce(c.ie_ciente,x.ie_ciente),
			x.nr_seq_dieta_cpoe,
			substr(obter_dil_comp_prescr_material(x.nr_prescricao,x.nr_sequencia,x.nr_agrupamento,x.ie_via_aplicacao,x.cd_intervalo,x.ie_agrupador),1,2000) ds_observacao
	from	prescr_material x,
			prescr_mat_hor c,
			prescr_medica a
	where	x.nr_prescricao		= c.nr_prescricao
	and		x.nr_sequencia		= c.nr_seq_material
	and		x.nr_prescricao		= a.nr_prescricao
	and		obter_se_exibir_rep_adep_setor(cd_setor_paciente_p,a.cd_setor_atendimento,coalesce(a.ie_adep,'S')) = 'S'
	and		a.nr_atendimento	= nr_atendimento_p
	and		a.dt_validade_prescr between dt_validade_limite_p and dt_fim_w
	and		((obter_se_prescr_lib_adep(coalesce(a.dt_liberacao_medico,x.dt_lib_material), coalesce(a.dt_liberacao,x.dt_lib_material), coalesce(a.dt_liberacao_farmacia,x.dt_lib_material), ie_data_lib_prescr_p, a.ie_lib_farm) = 'S') or
			((ie_exibe_sem_lib_farm_p = 'S') and (coalesce(a.ie_prescr_nutricao, 'N') = 'S')))
	and		x.ie_agrupador		= 12
	and		((ie_exibir_suspensos_p = 'S') or (coalesce(x.dt_suspensao::text, '') = ''))
	and		coalesce(c.ie_situacao,'A')	= 'A'
	and 	((ie_utiliza_serv_w <> 'S') or
			 exists (SELECT 1 from nut_atend_serv_dia s where s.nr_seq_horario =  c.nr_sequencia) or (get_servico_sem_dieta = 'S'))
	and		c.ie_agrupador		= 12
	and		((coalesce(a.ie_recem_nato,'N')	= 'N') or (ie_recem_nato_w = 'S'))	
	and		((coalesce(c.ie_horario_especial,'N') = 'N') or (c.dt_fim_horario IS NOT NULL AND c.dt_fim_horario::text <> ''))
	and		((ie_exibir_hor_realizados_p = 'S') or (coalesce(c.dt_fim_horario::text, '') = ''))
	and		((ie_exibir_hor_suspensos_p = 'S') or (coalesce(c.dt_suspensao::text, '') = ''))
	and		((ie_regra_inclusao_p = 'S') or
			 ((ie_regra_inclusao_p = 'R') and (adep_obter_regra_inclusao(	'SO', 
																		cd_estabelecimento_p, 
																		cd_setor_usuario_p, 
																		cd_perfil_p, 
																		c.cd_material, 
																		null, 
																		null, 
																		null,
																		a.cd_Setor_atendimento,
																		null,
																		a.nr_prescricao,
																		null) = 'S'))) -- nr_seq_exame_p
	and		c.dt_horario between dt_horario_inicio_sinc_w and dt_horario_fim_sinc_w
	and		((ie_prescr_setor_p = 'N') or (ie_prescr_setor_p = 'S' AND a.cd_setor_atendimento = cd_setor_paciente_p))
	and		Obter_se_horario_liberado(c.dt_lib_horario, c.dt_horario) = 'S'
	and		((get_ie_vigente = 'N') or (clock_timestamp() between a.dt_inicio_prescr and a.dt_validade_prescr + get_minutos_vigente/1440))
	group by
		c.dt_horario, 
		a.nr_prescricao,
		c.nr_seq_material,
		c.nr_sequencia,
		c.dt_fim_horario,
		c.dt_primeira_checagem,
		c.dt_bloqueio,
		c.dt_suspensao,
		c.cd_material,
		x.ie_acm,
		x.ie_se_necessario,
		x.cd_intervalo,
		x.qt_dose,
		x.ie_suspenso,
		c.dt_fim_horario,
		c.dt_suspensao,
		c.ie_dose_especial,
		c.nr_seq_processo,
		c.nr_seq_area_prep,
		coalesce(c.ie_ciente,x.ie_ciente),
		x.nr_seq_dieta_cpoe,
		x.nr_prescricao,
        x.nr_sequencia,
        x.nr_agrupamento,
        x.ie_via_aplicacao,
        x.ie_agrupador
	order by
		c.dt_horario,
		c.dt_suspensao;
	
BEGIN
	ds_sep_bv_w	:= obter_separador_bv;
	open c01;
	loop
	fetch c01 into	current_setting('adep_gerar_horarios_pck.nr_prescricao_w')::prescr_medica.nr_prescricao%type,
			nr_seq_material_w,
			nr_seq_horario_w,
			ie_status_horario_w,
			cd_material_w,
			ie_acm_sn_w,
			cd_intervalo_w,
			qt_dose_w,
			ds_prescricao_w,
			ie_status_w,
			current_setting('adep_gerar_horarios_pck.dt_horario_w')::timestamp,
			ie_ciente_w,
			nr_seq_dieta_cpoe_w,
			ds_observacao_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		nr_horario_w := adep_gerar_horarios_pck.get_posicao_horario(current_setting('adep_gerar_horarios_pck.dt_horario_w')::timestamp);
		if (nr_horario_w IS NOT NULL AND nr_horario_w::text <> '') then
			ds_comando_update_w	:=	' update w_adep_t ' ||
							' set hora' || to_char(nr_horario_w) || ' = :vl_hora, ' ||
							' nr_prescricoes = adep_juntar_prescricao(nr_prescricoes,:nr_prescricao), ' ||
							' ie_ciencia = :ie_ciencia' ||
							' where nm_usuario = :nm_usuario ' ||
							' and ie_tipo_item = :ie_tipo ' ||
							' and nvl(nr_prescricao,nvl(:nr_prescricao,0)) = nvl(:nr_prescricao,0) ' ||
							' and nvl(nr_seq_cpoe,nvl(:nr_seq_cpoe,0)) = nvl(:nr_seq_cpoe,0) ' ||
							' and nvl(nr_seq_item,nvl(:nr_seq_item,0)) = nvl(:nr_seq_item,0) ' ||
							' and cd_item = :cd_item ' ||
							' and nvl(cd_intervalo,0) = nvl(:cd_intervalo,0) ' ||
							' and nvl(qt_item,0) = nvl(:qt_item,0) ' ||
							' and ((ds_diluicao = :ds_observacao) or (ds_diluicao is null)) ' ||
							' and (nvl(ie_status_item,''N'') = nvl(:ie_status,''N'')) ' ||
							' and ((ds_prescricao = :ds_prescricao) or (ds_prescricao is null)) ';
			CALL exec_sql_dinamico_bv('ADEP', ds_comando_update_w,	'vl_hora=S' || to_char(nr_seq_horario_w) || 'H' || ie_status_horario_w || ds_sep_bv_w ||
										'nr_prescricao=' || to_char(current_setting('adep_gerar_horarios_pck.nr_prescricao_w')::prescr_medica.nr_prescricao%type) || ds_sep_bv_w ||
										'nr_seq_cpoe=' || to_char(nr_seq_dieta_cpoe_w) || ds_sep_bv_w ||
										'ie_ciencia=' || ie_ciente_w || ds_sep_bv_w ||
										'nm_usuario=' || nm_usuario_p || ds_sep_bv_w ||
										'ie_tipo=S' || ds_sep_bv_w ||
										'nr_seq_item='|| to_char(nr_seq_material_w) || ds_sep_bv_w ||
										'cd_item=' || to_char(cd_material_w) || ds_sep_bv_w ||
										'cd_intervalo=' || cd_intervalo_w  || ds_sep_bv_w ||
										'qt_item=' || to_char(qt_dose_w) || ds_sep_bv_w ||
										'ds_observacao=' || ds_observacao_w || ds_sep_bv_w ||
										'ie_status=' || ie_status_w || ds_sep_bv_w ||
										'ds_prescricao=' || ds_prescricao_w || ds_sep_bv_w );
			commit;
		end if;
		end;
	end loop;
	close c01;
	CALL Atualizar_adep_controle(nm_usuario_p, nr_atendimento_p, 'S', 'N');
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE adep_gerar_horarios_pck.adep_sincronizar_supl_oral ( cd_estabelecimento_p bigint, cd_setor_usuario_p bigint, cd_perfil_p bigint, nm_usuario_p text, nr_atendimento_p bigint, dt_inicial_horarios_p timestamp, dt_final_horarios_p timestamp, dt_validade_limite_p timestamp, ie_exibir_hor_realizados_p text, ie_exibir_hor_suspensos_p text, ie_regra_inclusao_p text, ie_data_lib_prescr_p text, ie_exibir_suspensos_p text, ie_agrupar_acm_sn_p text, dt_horario_p timestamp, nr_horario_p integer, ie_exibe_sem_lib_farm_p text, ie_prescr_setor_p text, cd_setor_paciente_p bigint) FROM PUBLIC;
