SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION adep_gerar_horarios_pck.obter_ds_proc_respiratorio (nr_seq_proc_cpoe_p bigint) RETURNS varchar AS $body$
DECLARE

	ie_disp_resp_esp_ww	varchar(254);
	ie_disp_resp_esp_w	cpoe_procedimento.ie_disp_resp_esp%type;
	qt_fio2_w			cpoe_procedimento.qt_fio2%type;
	qt_pressao_w		cpoe_procedimento.qt_pressao%type;
	ds_dado_clinico_w	cpoe_procedimento.ds_dado_clinico%type;
	ds_respiratorio_w	varchar(2000);
	
BEGIN
		select	max(ie_disp_resp_esp),
				max(qt_fio2),
				max(qt_pressao),
				max(ds_dado_clinico)
		into STRICT	ie_disp_resp_esp_w,
				qt_fio2_w,
				qt_pressao_w,
				ds_dado_clinico_w
		from cpoe_procedimento p
		where p.nr_sequencia = nr_seq_proc_cpoe_p;
		
		if (ie_disp_resp_esp_w IS NOT NULL AND ie_disp_resp_esp_w::text <> '') then
			ie_disp_resp_esp_ww := obter_valor_dominio(9785, ie_disp_resp_esp_w);
			if (coalesce(qt_pressao_w::text, '') = '') then
				--Aerosolterapia
				/*
				Dispositivo de respiracao:#@IE_DISP_RESP#@
				FiO: #@QT_FIO2#@%
				*/
				ds_respiratorio_w := substr(chr(10) || wheb_mensagem_pck.get_texto(1134478,'IE_DISP_RESP=' || ie_disp_resp_esp_ww || ';QT_FIO2=' || qt_fio2_w) || chr(10),1,2000);
			elsif (qt_pressao_w IS NOT NULL AND qt_pressao_w::text <> '') then
				--Fisioterapia / Expansao pulmonar
				/*
				Dispositivo de respiracao:#@IE_DISP_RESP#@
				Pressao: #@QT_PRESSAO#@% cmH20
				Indicacao clinica: #@DS_INDICACAO_CLINICA#@
				*/
				ds_respiratorio_w := substr(chr(10) || wheb_mensagem_pck.get_texto(1134479,'IE_DISP_RESP=' || ie_disp_resp_esp_ww || ';QT_PRESSAO=' || qt_pressao_w|| ';DS_INDICACAO_CLINICA=' || ds_dado_clinico_w) || chr(10),1,2000);
			end if;
		end if;

		return ds_respiratorio_w;
	end;


$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
