CREATE OR REPLACE PROCEDURE adep_onc_gerar_horarios_pck.adep_onc_obter_medic ( nm_usuario_p text, nr_atendimento_p bigint, ie_data_lib_prescr_p text, ie_exibir_suspensos_p text, ie_agrupar_acm_sn_p text, ie_agrupa_suspensos_p text, ie_opcao_filtro_p text, dt_filtro_p timestamp, nr_ciclo_p bigint, ie_lib_pend_rep_p text, dt_inicial_horario_p timestamp, dt_final_horario_p timestamp) AS $body$
DECLARE


	nr_seq_wadep_w			bigint;
	nr_prescricao_w			bigint;
	nr_seq_material_w		integer;
	cd_material_w			integer;
	ds_material_w			varchar(255);
	ie_acm_sn_w			varchar(1);
	cd_intervalo_w			varchar(7);
	qt_dose_w			double precision;
	nr_agrupamento_w		double precision;
	ds_prescricao_w			varchar(240);
	ie_status_w			varchar(1);
	ie_via_aplicacao_w		varchar(5);
	cd_unidade_medida_dose_w	varchar(30);
	nr_ciclo_w			smallint;
	ie_pre_medicacao_w		varchar(1);
	ds_dia_ciclo_w			varchar(5);
	nr_seq_onc_w			bigint;
	dt_oncologia_w			timestamp;
	nr_seq_mat_cpoe_w		prescr_material.nr_seq_mat_cpoe%type;
	ie_composto_w			varchar(1);
	
	c01 CURSOR FOR
	SELECT	CASE WHEN ie_acm_sn='S' THEN  CASE WHEN obter_se_agrupa_acm_sn_medic(nr_prescricao,nr_seq_material,ie_agrupar_acm_sn_p)='N' THEN  nr_prescricao  ELSE null END   ELSE null END ,
		CASE WHEN ie_acm_sn='S' THEN  CASE WHEN obter_se_agrupa_acm_sn_medic(nr_prescricao,nr_seq_material,ie_agrupar_acm_sn_p)='N' THEN  nr_seq_material  ELSE null END   ELSE null END ,
		cd_material,
		ds_material,
		ie_acm_sn,
		cd_intervalo,
		qt_dose,
		nr_agrupamento,
		ds_prescricao,
		CASE WHEN ie_acm_sn='S' THEN  CASE WHEN obter_se_agrupa_acm_sn_medic(nr_prescricao,nr_seq_material,ie_agrupar_acm_sn_p)='N' THEN  CASE WHEN ie_suspenso='S' THEN  ie_suspenso  ELSE null END   ELSE null END  WHEN ie_acm_sn='N' THEN  CASE WHEN ie_agrupa_suspensos_p='N' THEN  ie_suspenso  ELSE null END   ELSE null END  ie_status,
		ie_via_aplicacao,
		cd_unidade_medida_dose,
		nr_ciclo,
		ds_dia_ciclo,
		nr_seq_ordem_adep,
		ie_pre_medicacao,
		dt_oncologia,
		nr_seq_mat_cpoe,
		CASE WHEN ie_suspenso='S' THEN null  ELSE ie_composto END  ie_composto
	from	(
		SELECT	a.nr_prescricao,
			c.nr_seq_material,
			c.cd_material,
			VIPE_obter_se_medic_composto(a.nr_prescricao, x.nr_sequencia, x.nr_agrupamento, 'D') ds_material,
			obter_se_acm_sn(x.ie_acm,x.ie_se_necessario) ie_acm_sn,
			x.cd_intervalo,
			x.qt_dose,
			CASE WHEN obter_se_agrupa_composto(a.nr_prescricao,c.nr_seq_material,x.nr_agrupamento,c.cd_material)='S' THEN x.nr_agrupamento  ELSE 0 END  nr_agrupamento,
			substr(adep_obter_um_dosagem_prescr(a.nr_prescricao,c.nr_seq_material,x.ie_acm,x.ie_se_necessario),1,100) ds_prescricao,
			coalesce(x.ie_suspenso,'N') ie_suspenso,
			x.ie_via_aplicacao,
			x.cd_unidade_medida_dose,
			b.nr_ciclo,
			coalesce(b.ds_dia_ciclo, b.ds_dia_ciclo_real) ds_dia_ciclo,
			coalesce(x.nr_Seq_ordem_adep,x.nr_agrupamento) nr_seq_ordem_adep,
			coalesce(x.ie_pre_medicacao,'N') ie_pre_medicacao,
			coalesce(b.dt_real,b.dt_prevista) dt_oncologia,
			x.nr_seq_mat_cpoe,
			VIPE_obter_se_medic_composto(a.nr_prescricao, x.nr_sequencia, x.nr_agrupamento, 'A') ie_composto
		from	material y,
			prescr_material x,
			prescr_mat_hor c,
			prescr_medica a,
			paciente_atendimento b,
			paciente_setor p
		where	y.cd_material = x.cd_material
		and	x.nr_prescricao = c.nr_prescricao
		and	x.nr_sequencia = c.nr_seq_material
		and	x.nr_prescricao = a.nr_prescricao
		and	c.nr_prescricao = a.nr_prescricao
		and	a.nr_seq_atend	= b.nr_seq_atendimento
		and	b.nr_seq_paciente = p.nr_seq_paciente
		and	a.nr_atendimento = nr_atendimento_p
		and	a.cd_pessoa_fisica = p.cd_pessoa_fisica
		and	coalesce(a.ie_recem_nato,'N')	= 'N'
		and	obter_se_prescr_lib_adep(coalesce(a.dt_liberacao_medico,x.dt_lib_material), coalesce(a.dt_liberacao,x.dt_lib_material), coalesce(a.dt_liberacao_farmacia,x.dt_lib_material), ie_data_lib_prescr_p) = 'S'
		and	x.ie_agrupador = 1
		and	((ie_exibir_suspensos_p = 'S') or (coalesce(x.dt_suspensao::text, '') = ''))
		and	coalesce(c.ie_situacao,'A') = 'A'
		and	coalesce(c.ie_adep,'S') = 'S'
		and	coalesce(x.nr_seq_kit::text, '') = ''
		and	c.ie_agrupador = 1
		and	Obter_se_horario_liberado(c.dt_lib_horario, c.dt_horario) = 'S'
		and	obter_se_gerar_item_adep('M',a.nr_prescricao,c.nr_seq_material,x.nr_agrupamento,getie_apres_kit) = 'S'
		and ((ie_opcao_filtro_p in ('Q','C')) or
			 ((ie_opcao_filtro_p = 'D') and (coalesce(b.dt_real,b.dt_prevista) between trunc(dt_filtro_p) and fim_dia(dt_filtro_p))) or
			 (ie_opcao_filtro_p = 'H' AND c.dt_horario between dt_inicial_horario_p and dt_final_horario_p))
		and	b.nr_ciclo	= coalesce(nr_ciclo_p,b.nr_ciclo)		
		group by
			a.nr_prescricao,
			c.nr_seq_material,
			c.cd_material,
			VIPE_obter_se_medic_composto(a.nr_prescricao, x.nr_sequencia, x.nr_agrupamento, 'D'),
			x.ie_acm,
			x.ie_se_necessario,
			x.cd_intervalo,
			x.qt_dose,
			x.nr_agrupamento,
			x.ie_suspenso,
			x.ie_via_aplicacao,
			x.cd_unidade_medida_dose,
			b.nr_ciclo,
			coalesce(b.ds_dia_ciclo, b.ds_dia_ciclo_real),
			coalesce(x.nr_Seq_ordem_adep,x.nr_agrupamento),
			coalesce(x.ie_pre_medicacao,'N'),
			coalesce(b.dt_real,b.dt_prevista),
			x.nr_seq_mat_cpoe,
			VIPE_obter_se_medic_composto(a.nr_prescricao, x.nr_sequencia, x.nr_agrupamento, 'A')
		) alias45
