		CASE WHEN ie_acm_sn='S' THEN  CASE WHEN obter_se_agrupa_acm_sn_medic(nr_prescricao,nr_seq_material,ie_agrupar_acm_sn_p)='N' THEN  CASE WHEN ie_suspenso='S' THEN  ie_suspenso  ELSE null END   ELSE null END  WHEN ie_acm_sn='N' THEN  CASE WHEN ie_agrupa_suspensos_p='N' THEN  ie_suspenso  ELSE null END   ELSE null END ,
		ie_via_aplicacao,
