		nr_ciclo,
		ds_dia_ciclo,
		nr_seq_ordem_adep,
		ie_pre_medicacao,
		dt_oncologia,
		nr_seq_mat_cpoe,
		CASE WHEN ie_suspenso='S' THEN null  ELSE ie_composto END;		

	
BEGIN
	
