			cd_intervalo_w,
			qt_dose_w,
			nr_agrupamento_w,
			ds_prescricao_w,
			ie_status_w,
			ie_via_aplicacao_w,
			cd_unidade_medida_dose_w,
			nr_ciclo_w,
			ds_dia_ciclo_w,
			nr_seq_onc_w,
			ie_pre_medicacao_w,
