-- REVOKE ALL ON PROCEDURE adep_onc_gerar_horarios_pck.adep_onc_obter_medic ( nm_usuario_p text, nr_atendimento_p bigint, ie_data_lib_prescr_p text, ie_exibir_suspensos_p text, ie_agrupar_acm_sn_p text, ie_agrupa_suspensos_p text, ie_opcao_filtro_p text, dt_filtro_p timestamp, nr_ciclo_p bigint, ie_lib_pend_rep_p text, dt_inicial_horario_p timestamp, dt_final_horario_p timestamp) FROM PUBLIC;
