CREATE OR REPLACE PROCEDURE adep_onc_gerar_horarios_pck.adep_onc_obter_rec ( nm_usuario_p text, nr_atendimento_p bigint, ie_data_lib_prescr_p text, ie_exibir_suspensos_p text, ie_agrupar_acm_sn_p text, ie_opcao_filtro_p text, dt_filtro_p timestamp, nr_ciclo_p bigint, ie_lib_pend_rep_p text, dt_inicial_horario_p timestamp, dt_final_horario_p timestamp) AS $body$
DECLARE


	nr_seq_wadep_w		bigint;
	nr_prescricao_w		bigint;
	nr_seq_recomendacao_w	integer;
	cd_recomendacao_w	varchar(255);
	ds_diluicao_w		varchar(2000);
	ds_recomendacao_w	varchar(4000);
	ie_acm_sn_w		varchar(255);
	cd_intervalo_w		varchar(255);
	qt_recomendacao_w	double precision;
	ds_intervalo_w		varchar(255);
