	ie_horario_w		varchar(255);
	nr_ciclo_w		smallint;
	ds_dia_ciclo_w		varchar(5);
	nr_seq_onc_w		bigint;
	dt_oncologia_w		timestamp;
	nr_seq_rec_cpoe_w	prescr_recomendacao.nr_seq_rec_cpoe%type;	
	
	c01 CURSOR FOR
	SELECT	CASE WHEN getAgrupaRec='N' THEN  nr_prescricao  ELSE CASE WHEN ie_acm_sn='S' THEN  CASE WHEN obter_se_agrupar_acm_sn_adep(nr_prescricao,nr_seq_recomendacao,ie_agrupar_acm_sn_p)='N' THEN  nr_prescricao  ELSE null END   ELSE null END  END ,
		CASE WHEN getAgrupaRec='N' THEN  nr_seq_recomendacao  ELSE CASE WHEN ie_acm_sn='S' THEN  CASE WHEN obter_se_agrupar_acm_sn_adep(nr_prescricao,nr_seq_recomendacao,ie_agrupar_acm_sn_p)='N' THEN  nr_seq_recomendacao  ELSE null END   ELSE null END  END ,	
		substr(cd_recomendacao,1,255),
