			coalesce(y.ds_tipo_recomendacao,x.ds_recomendacao) ds_recomendacao,
			obter_se_acm_sn(x.ie_acm,x.ie_se_necessario) ie_acm_sn,		
			x.cd_intervalo,
			x.qt_recomendacao,
			w.ds_prescricao ds_prescricao,
			coalesce(x.ie_suspenso,'N') ie_suspenso,
			substr(CASE WHEN coalesce(x.ie_faose::text, '') = '' THEN ''  ELSE 'FAOSE: ' END  || substr(obter_valor_dominio(3554, x.ie_faose),1,50) || '  ' || substr(x.DS_RECOMENDACAO,1,3400),1,1000) ds_diluicao,
			b.nr_ciclo,
			coalesce(b.ds_dia_ciclo, b.ds_dia_ciclo_real) ds_dia_ciclo,
			coalesce(x.nr_seq_ordem_adep,0) nr_seq_ordem_adep,
			coalesce(b.dt_real,b.dt_prevista) dt_oncologia,
			x.nr_seq_rec_cpoe
		FROM paciente_setor p, prescr_rec_hor c, paciente_atendimento b, prescr_medica a, prescr_recomendacao x
LEFT OUTER JOIN intervalo_prescricao w ON (x.cd_intervalo = w.cd_intervalo)
LEFT OUTER JOIN tipo_recomendacao y ON (x.cd_recomendacao = y.cd_tipo_recomendacao)
WHERE x.nr_prescricao = c.nr_prescricao and x.nr_sequencia = c.nr_seq_recomendacao and x.nr_prescricao = a.nr_prescricao and c.nr_prescricao = a.nr_prescricao and a.nr_seq_atend	= b.nr_seq_atendimento and b.nr_seq_paciente = p.nr_seq_paciente and a.nr_atendimento = nr_atendimento_p and p.cd_pessoa_fisica = a.cd_pessoa_fisica and ((ie_data_lib_prescr_p = 'F') or (obter_se_prescr_lib_adep(a.dt_liberacao_medico, a.dt_liberacao, a.dt_liberacao_farmacia, ie_data_lib_prescr_p) = 'S')) and ((ie_exibir_suspensos_p = 'S') or (coalesce(x.dt_suspensao::text, '') = '')) and coalesce(c.ie_situacao,'A') = 'A' and coalesce(a.ie_recem_nato,'N')	= 'N' and Obter_se_horario_liberado(c.dt_lib_horario, c.dt_horario) = 'S' and ((ie_opcao_filtro_p in ('Q','C')) or
			 ((ie_opcao_filtro_p = 'D') and (coalesce(b.dt_real,b.dt_prevista) between trunc(dt_filtro_p) and fim_dia(dt_filtro_p))) or
			 (ie_opcao_filtro_p = 'H' AND c.dt_horario between dt_inicial_horario_p and dt_final_horario_p)) and b.nr_ciclo	= coalesce(nr_ciclo_p,b.nr_ciclo) group by
			a.nr_prescricao,
			c.nr_seq_recomendacao,
			x.cd_recomendacao,
			x.ds_recomendacao,
			coalesce(x.ds_recomendacao,' '),
			y.ds_tipo_recomendacao,
			x.ds_horarios,
			x.ie_se_necessario,
			x.cd_intervalo,
			x.qt_recomendacao,
			w.ds_prescricao,
