			x.ie_acm,
			substr(CASE WHEN coalesce(x.ie_faose::text, '') = '' THEN ''  ELSE 'FAOSE: ' END  || substr(obter_valor_dominio(3554, x.ie_faose),1,50) || '  ' || substr(x.DS_RECOMENDACAO,1,3400),1,1000),
			b.nr_ciclo,
			coalesce(b.ds_dia_ciclo, b.ds_dia_ciclo_real),
