			coalesce(b.dt_real,b.dt_prevista),
			x.nr_seq_rec_cpoe
		) alias53
	group by
		CASE WHEN getAgrupaRec='N' THEN  nr_prescricao  ELSE CASE WHEN ie_acm_sn='S' THEN  CASE WHEN obter_se_agrupar_acm_sn_adep(nr_prescricao,nr_seq_recomendacao,ie_agrupar_acm_sn_p)='N' THEN  nr_prescricao  ELSE null END   ELSE null END  END ,
		CASE WHEN getAgrupaRec='N' THEN  nr_seq_recomendacao  ELSE CASE WHEN ie_acm_sn='S' THEN  CASE WHEN obter_se_agrupar_acm_sn_adep(nr_prescricao,nr_seq_recomendacao,ie_agrupar_acm_sn_p)='N' THEN  nr_seq_recomendacao  ELSE null END   ELSE null END  END ,
		cd_recomendacao,
