LEFT OUTER JOIN tipo_recomendacao y ON (x.cd_recomendacao = y.cd_tipo_recomendacao)
WHERE x.nr_prescricao = a.nr_prescricao and a.nr_seq_atend	= b.nr_seq_atendimento and b.nr_seq_paciente = p.nr_seq_paciente and a.nr_atendimento = nr_atendimento_p and a.cd_pessoa_fisica = p.cd_pessoa_fisica and coalesce(x.dt_suspensao::text, '') = '' and coalesce(a.dt_suspensao::text, '') = '' and (a.dt_liberacao_medico IS NOT NULL AND a.dt_liberacao_medico::text <> '') and ((coalesce(a.dt_liberacao::text, '') = '') or (coalesce(a.dt_liberacao_farmacia::text, '') = '')) and ((ie_exibir_suspensos_p = 'S') or (coalesce(x.dt_suspensao::text, '') = '')) and coalesce(a.ie_recem_nato,'N')	= 'N' and ((ie_opcao_filtro_p	in ('Q','C')) or (coalesce(b.dt_real,b.dt_prevista) between trunc(dt_filtro_p) and fim_dia(dt_filtro_p))) and b.nr_ciclo	= coalesce(nr_ciclo_p,b.nr_ciclo) and not exists (
				select	1
				from	prescr_rec_hor c
				where	c.nr_prescricao = x.nr_prescricao
				and	c.nr_seq_recomendacao = x.nr_sequencia
				and	c.nr_prescricao = a.nr_prescricao
				and	Obter_se_horario_liberado(c.dt_lib_horario, c.dt_horario) = 'S') group by
			a.nr_prescricao,
