SET client_encoding TO 'UTF8';




CREATE OR REPLACE PROCEDURE adep_onc_gerar_horarios_pck.adep_onc_sincronizar_proced ( nm_usuario_p text, nr_atendimento_p bigint, ie_exibir_suspensos_p text, ie_data_lib_proced_p text, ie_opcao_filtro_p text, dt_filtro_p timestamp, nr_ciclo_p bigint, dt_inicial_horario_p timestamp, dt_final_horario_p timestamp) AS $body$
DECLARE

				
	ds_sep_bv_w		varchar(50);				
	nr_prescricao_w		bigint;
	nr_seq_procedimento_w	numeric(20);
	nr_seq_horario_w	bigint;
	ie_status_horario_w	varchar(15);
	cd_procedimento_w	bigint;
	nr_seq_proc_interno_w	bigint;
	cd_intervalo_w		varchar(7);
	qt_procedimento_w	double precision;
	ds_prescricao_w		varchar(240);
	ds_mat_med_assoc_w	varchar(2000);
	ds_comando_update_w	varchar(4000);
	current_setting('adep_onc_gerar_horarios_pck.dt_horario_w')::timestamp		timestamp;
	nr_horario_w		integer;
	nr_ciclo_w		smallint;
	ds_dia_ciclo_w		varchar(5);
	nr_seq_onc_w		bigint;	
	dt_oncologia_w		timestamp;
	nr_seq_proc_cpoe_w	prescr_procedimento.nr_seq_proc_cpoe%type;	
	
	c01 CURSOR FOR	
	SELECT	a.nr_prescricao,
		CASE WHEN substr(obter_se_associado_dif(a.nr_prescricao,c.nr_seq_procedimento),1,1)='N' THEN c.nr_seq_procedimento||a.nr_prescricao  ELSE c.nr_seq_procedimento END ,
		c.nr_sequencia,
		CASE WHEN coalesce(c.dt_primeira_checagem::text, '') = '' THEN substr(obter_status_hor_proced2(c.dt_fim_horario,c.dt_suspensao,c.ie_dose_especial,c.dt_inicio_proced),1,15)  ELSE 'Q' END ,
		c.cd_procedimento,
		c.nr_seq_proc_interno,
		x.cd_intervalo,
		x.qt_procedimento,
		substr(adep_obter_dados_prescr_proc(a.nr_prescricao,c.nr_seq_procedimento,'QIL','S',x.ie_acm,x.ie_se_necessario),1,100) ds_prescricao,
		substr(x.ds_observacao || '  ' || CASE WHEN coalesce(coalesce(x.nr_seq_prescr_mat,x.nr_seq_prescr_sol)::text, '') = '' THEN obter_mat_med_assoc_proc(a.nr_prescricao,c.nr_seq_procedimento,'S')  ELSE '' END  || '  ' || Obter_desc_item_assoc_proc(coalesce(x.nr_seq_prescr_mat,nr_seq_prescr_sol),x.nr_prescricao,CASE WHEN coalesce(nr_seq_prescr_mat::text, '') = '' THEN 'S'  ELSE 'M' END ) ,1,2000) ds_mat_med_assoc,
		c.dt_horario,
		b.nr_ciclo,
		coalesce(b.ds_dia_ciclo_real, b.ds_dia_ciclo) ds_dia_ciclo,
		coalesce(coalesce(x.nr_seq_ordem_adep,x.nr_agrupamento),0) nr_seq_ordem_adep,
		coalesce(b.dt_real, b.dt_prevista) dt_oncologia,
		x.nr_seq_proc_cpoe
	from	procedimento y,
		prescr_procedimento x,
		prescr_proc_hor c,
		prescr_medica a,
		paciente_atendimento b,
		paciente_setor p
	where	y.cd_procedimento = x.cd_procedimento
	and	y.ie_origem_proced = x.ie_origem_proced
	and	y.cd_procedimento = c.cd_procedimento
	and	y.ie_origem_proced = c.ie_origem_proced			
	and	x.nr_prescricao = c.nr_prescricao
	and	x.nr_sequencia = c.nr_seq_procedimento
	and	x.nr_prescricao = a.nr_prescricao
	and	c.nr_prescricao = a.nr_prescricao
	and	a.nr_seq_atend	= b.nr_seq_atendimento
	and	b.nr_seq_paciente = p.nr_seq_paciente
	and	coalesce(x.nr_seq_proc_princ::text, '') = ''
	and	a.nr_atendimento = nr_atendimento_p
	and	p.cd_pessoa_fisica = a.cd_pessoa_fisica
	and	(obter_data_lib_proc_adep(a.dt_liberacao, a.dt_liberacao_medico, ie_data_lib_proced_p) IS NOT NULL AND (obter_data_lib_proc_adep(a.dt_liberacao, a.dt_liberacao_medico, ie_data_lib_proced_p))::text <> '')
	and	coalesce(x.nr_seq_proc_interno::text, '') = ''
	and	coalesce(x.nr_seq_exame::text, '') = ''
	and	coalesce(x.nr_seq_solic_sangue::text, '') = ''
	and 	coalesce(x.nr_Seq_origem::text, '') = ''
	and	coalesce(x.nr_seq_derivado::text, '') = ''
	and	coalesce(x.nr_seq_exame_sangue::text, '') = ''
	and	coalesce(a.ie_recem_nato,'N')	= 'N'
	and	((ie_exibir_suspensos_p = 'S') or (coalesce(x.dt_suspensao::text, '') = ''))
	and	coalesce(c.ie_situacao,'A') = 'A'
	and	((coalesce(c.ie_horario_especial,'N') = 'N') or (c.dt_fim_horario IS NOT NULL AND c.dt_fim_horario::text <> ''))
	and	Obter_se_horario_liberado(c.dt_lib_horario, c.dt_horario) = 'S'
	and ((ie_opcao_filtro_p in ('Q','C')) or
		 ((ie_opcao_filtro_p = 'D') and (coalesce(b.dt_real,b.dt_prevista) between trunc(dt_filtro_p) and fim_dia(dt_filtro_p))) or
         (ie_opcao_filtro_p = 'H' AND c.dt_horario between dt_inicial_horario_p and dt_final_horario_p))
	and	b.nr_ciclo	= coalesce(nr_ciclo_p,b.nr_ciclo)	
	group by
		c.dt_horario,
		a.nr_prescricao,
		c.nr_seq_procedimento,
		c.nr_sequencia,
		c.dt_fim_horario,
		c.dt_suspensao,
		c.ie_dose_especial,
		c.cd_procedimento,
		c.dt_primeira_checagem,
		y.ds_procedimento,
		c.nr_seq_proc_interno,
		x.ie_acm,
		substr(x.ds_observacao || '  ' || CASE WHEN coalesce(coalesce(x.nr_seq_prescr_mat,x.nr_seq_prescr_sol)::text, '') = '' THEN obter_mat_med_assoc_proc(a.nr_prescricao,c.nr_seq_procedimento,'S')  ELSE '' END  || '  ' || Obter_desc_item_assoc_proc(coalesce(x.nr_seq_prescr_mat,nr_seq_prescr_sol),x.nr_prescricao,CASE WHEN coalesce(nr_seq_prescr_mat::text, '') = '' THEN 'S'  ELSE 'M' END ) ,1,2000),
		x.ie_se_necessario,		
		x.cd_intervalo,
		x.qt_procedimento,
		c.dt_inicio_proced,
		x.ie_status_atend,
		b.nr_ciclo,
		coalesce(b.ds_dia_ciclo_real, b.ds_dia_ciclo),
		coalesce(coalesce(x.nr_seq_ordem_adep,x.nr_agrupamento),0),
		coalesce(b.dt_real, b.dt_prevista),
		x.nr_seq_proc_cpoe
	
union all

	SELECT	a.nr_prescricao,
		CASE WHEN substr(obter_se_associado_dif(a.nr_prescricao,c.nr_seq_procedimento),1,1)='N' THEN c.nr_seq_procedimento||a.nr_prescricao  ELSE c.nr_seq_procedimento END ,
		c.nr_sequencia,
		CASE WHEN coalesce(c.dt_primeira_checagem::text, '') = '' THEN substr(obter_status_hor_proced2(c.dt_fim_horario,c.dt_suspensao,c.ie_dose_especial,c.dt_inicio_proced),1,15)  ELSE 'Q' END ,
		c.cd_procedimento,
		c.nr_seq_proc_interno,
		x.cd_intervalo,
		x.qt_procedimento,
		substr(adep_obter_dados_prescr_proc(a.nr_prescricao,c.nr_seq_procedimento,'QIL','S',x.ie_acm,x.ie_se_necessario),1,100) ds_prescricao,
		substr(x.ds_observacao || '  ' || CASE WHEN coalesce(coalesce(x.nr_seq_prescr_mat,x.nr_seq_prescr_sol)::text, '') = '' THEN obter_mat_med_assoc_proc(a.nr_prescricao,c.nr_seq_procedimento,'S')  ELSE '' END  || '  ' || Obter_desc_item_assoc_proc(coalesce(x.nr_seq_prescr_mat,nr_seq_prescr_sol),x.nr_prescricao,CASE WHEN coalesce(nr_seq_prescr_mat::text, '') = '' THEN 'S'  ELSE 'M' END ) ,1,2000),
		c.dt_horario,
		b.nr_ciclo,
		coalesce(b.ds_dia_ciclo_real, b.ds_dia_ciclo) ds_dia_ciclo,
		coalesce(coalesce(x.nr_seq_ordem_adep,x.nr_agrupamento),0) nr_seq_ordem_adep,
		coalesce(b.dt_real, b.dt_prevista) dt_oncologia,
		x.nr_seq_proc_cpoe
	from	procedimento y,
		proc_interno w,
		prescr_procedimento x,
		prescr_proc_hor c,
		prescr_medica a,
		paciente_atendimento b,
		paciente_setor p
	where	y.cd_procedimento = x.cd_procedimento
	and	y.ie_origem_proced = x.ie_origem_proced
	and	y.cd_procedimento = c.cd_procedimento
	and	y.ie_origem_proced = c.ie_origem_proced		
	and	w.nr_sequencia = x.nr_seq_proc_interno
	and	w.nr_sequencia = c.nr_seq_proc_interno
	and	x.nr_prescricao = c.nr_prescricao
	and	x.nr_sequencia = c.nr_seq_procedimento
	and	x.nr_prescricao = a.nr_prescricao
	and	c.nr_prescricao = a.nr_prescricao
	and	a.nr_seq_atend	= b.nr_seq_atendimento
	and	b.nr_seq_paciente = p.nr_seq_paciente
	and	coalesce(a.ie_recem_nato,'N')	= 'N'
	and	a.nr_atendimento = nr_atendimento_p
	and	a.cd_pessoa_fisica = p.cd_pessoa_fisica
	and	(obter_data_lib_proc_adep(a.dt_liberacao, a.dt_liberacao_medico, ie_data_lib_proced_p) IS NOT NULL AND (obter_data_lib_proc_adep(a.dt_liberacao, a.dt_liberacao_medico, ie_data_lib_proced_p))::text <> '')
	and	w.ie_tipo <> 'G'
	and	w.ie_tipo <> 'BS'
	and	coalesce(x.nr_seq_proc_princ::text, '') = ''
	and	coalesce(w.ie_ivc,'N') <> 'S'
	and	coalesce(w.ie_ctrl_glic,'NC') = 'NC'
	and	(x.nr_seq_proc_interno IS NOT NULL AND x.nr_seq_proc_interno::text <> '')
	and	coalesce(x.nr_seq_prot_glic::text, '') = ''
	and	coalesce(x.nr_seq_exame::text, '') = ''
	and 	coalesce(x.nr_Seq_origem::text, '') = ''
	and	coalesce(x.nr_seq_solic_sangue::text, '') = ''
	and	coalesce(x.nr_seq_derivado::text, '') = ''
	and	coalesce(x.nr_seq_exame_sangue::text, '') = ''
	and	((ie_exibir_suspensos_p = 'S') or (coalesce(x.dt_suspensao::text, '') = ''))
	and	coalesce(c.ie_situacao,'A') = 'A'
	and	((coalesce(c.ie_horario_especial,'N') = 'N') or (c.dt_fim_horario IS NOT NULL AND c.dt_fim_horario::text <> ''))
	and	Obter_se_horario_liberado(c.dt_lib_horario, c.dt_horario) = 'S'
	and ((ie_opcao_filtro_p in ('Q','C')) or
		 ((ie_opcao_filtro_p = 'D') and (coalesce(b.dt_real,b.dt_prevista) between trunc(dt_filtro_p) and fim_dia(dt_filtro_p))) or
         (ie_opcao_filtro_p = 'H' AND c.dt_horario between dt_inicial_horario_p and dt_final_horario_p))
	and	b.nr_ciclo	= coalesce(nr_ciclo_p,b.nr_ciclo)	
	group by	
		c.dt_horario,
		a.nr_prescricao,
		c.nr_seq_procedimento,
		c.nr_sequencia,
		c.dt_fim_horario,
		c.dt_suspensao,
		c.ie_dose_especial,
		c.cd_procedimento,
		c.dt_primeira_checagem,
		w.ds_proc_exame,
		y.ds_procedimento,
		c.nr_seq_proc_interno,
		x.ie_acm,
		x.ie_se_necessario,	
		substr(x.ds_observacao || '  ' || CASE WHEN coalesce(coalesce(x.nr_seq_prescr_mat,x.nr_seq_prescr_sol)::text, '') = '' THEN obter_mat_med_assoc_proc(a.nr_prescricao,c.nr_seq_procedimento,'S')  ELSE '' END  || '  ' || Obter_desc_item_assoc_proc(coalesce(x.nr_seq_prescr_mat,nr_seq_prescr_sol),x.nr_prescricao,CASE WHEN coalesce(nr_seq_prescr_mat::text, '') = '' THEN 'S'  ELSE 'M' END ) ,1,2000),
		x.cd_intervalo,
		x.qt_procedimento,
		c.dt_inicio_proced,
		x.ie_status_atend,
		b.nr_ciclo,
		coalesce(b.ds_dia_ciclo_real, b.ds_dia_ciclo),
		coalesce(coalesce(x.nr_seq_ordem_adep,x.nr_agrupamento),0),
		coalesce(b.dt_real, b.dt_prevista),
		x.nr_seq_proc_cpoe
	order by
		dt_horario;
		
	
BEGIN
	ds_sep_bv_w	:= obter_separador_bv;
	open c01;
	loop
	fetch c01 into	nr_prescricao_w,
			nr_seq_procedimento_w,
			nr_seq_horario_w,
			ie_status_horario_w,
			cd_procedimento_w,
			nr_seq_proc_interno_w,
			cd_intervalo_w,
			qt_procedimento_w,
			ds_prescricao_w,
			ds_mat_med_assoc_w,
			current_setting('adep_onc_gerar_horarios_pck.dt_horario_w')::timestamp,
			nr_ciclo_w,
			ds_dia_ciclo_w,
			nr_seq_onc_w,
			dt_oncologia_w,
			nr_seq_proc_cpoe_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		nr_horario_w := adep_onc_gerar_horarios_pck.get_posicao_horario(current_setting('adep_onc_gerar_horarios_pck.dt_horario_w')::timestamp);
		if (nr_horario_w IS NOT NULL AND nr_horario_w::text <> '') then
			ds_comando_update_w	:=	' update w_adep_t ' ||
							' set hora' || to_char(nr_horario_w) || ' = :vl_hora, ' ||
							' nr_prescricoes = adep_juntar_prescricao(nr_prescricoes,:nr_prescricao), ' ||
							' ds_diluicao = :ds_mat_med_assoc ' ||					
							' where nm_usuario = :nm_usuario ' ||
							' and ie_tipo_item = :ie_tipo ' ||
							' and nvl(nr_prescricao,nvl(:nr_prescricao,0)) = nvl(:nr_prescricao,0) ' ||
							' and ((nvl(nr_seq_item,nvl(:nr_seq_item,0)) = nvl(:nr_seq_item,0)) or ' ||
							'      (nvl(nr_seq_item,nvl(:nr_seq_item||:nr_prescricao,0)) = nvl(:nr_seq_item||:nr_prescricao,0))) ' ||
							' and cd_item = :cd_item ' ||
							' and nvl(nr_seq_proc_interno,nvl(:nr_seq_proc_interno,0)) = nvl(:nr_seq_proc_interno,0) ' ||
							' and nvl(cd_intervalo,0) = nvl(:cd_intervalo,0) ' ||
							' and nvl(qt_item,0) = nvl(:qt_item,0) ' ||
							' and ((ds_prescricao = :ds_prescricao) or (ds_prescricao is null)) ' ||
							' and nr_ciclo = :nr_ciclo ' ||
							' and ds_dia_ciclo = :ds_dia_ciclo ' ||
							' and nr_seq_onc = :nr_seq_onc ' ||
							' and dt_oncologia = :dt_oncologia ' ||
							' and nr_seq_cpoe = :nr_seq_cpoe ';
						
			CALL exec_sql_dinamico_bv('ADEP', ds_comando_update_w,	'vl_hora=S' || to_char(nr_seq_horario_w) || 'H' || ie_status_horario_w || ds_sep_bv_w ||
										'nr_prescricao=' || to_char(nr_prescricao_w) || ds_sep_bv_w ||
										'ds_mat_med_assoc=' || ds_mat_med_assoc_w || ds_sep_bv_w || 
										'nm_usuario=' || nm_usuario_p || ds_sep_bv_w || 
										'ie_tipo=P' || ds_sep_bv_w ||
										'nr_seq_item=' || to_char(nr_seq_procedimento_w) || ds_sep_bv_w ||
										'cd_item=' || to_char(cd_procedimento_w) || ds_sep_bv_w ||
										'nr_seq_proc_interno=' || to_char(nr_seq_proc_interno_w) || ds_sep_bv_w ||
										'cd_intervalo=' || cd_intervalo_w  || ds_sep_bv_w ||
										'qt_item=' || to_char(qt_procedimento_w) || ds_sep_bv_w ||
										'ds_prescricao=' || ds_prescricao_w || ds_sep_bv_w ||
										'nr_ciclo=' || to_char(nr_ciclo_w) || ds_sep_bv_w ||
										'ds_dia_ciclo=' || ds_dia_ciclo_w || ds_sep_bv_w ||
										'nr_seq_onc=' || to_char(nr_seq_onc_w) || ds_sep_bv_w ||
										'dt_oncologia=' || to_char(dt_oncologia_w,'dd/mm/yyyy hh24:mi:ss') || ds_sep_bv_w ||
										'nr_seq_cpoe=' || to_char(nr_seq_proc_cpoe_w) || ds_sep_bv_w);
			commit;
		end if;
		end;
	end loop;
	close c01;
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE adep_onc_gerar_horarios_pck.adep_onc_sincronizar_proced ( nm_usuario_p text, nr_atendimento_p bigint, ie_exibir_suspensos_p text, ie_data_lib_proced_p text, ie_opcao_filtro_p text, dt_filtro_p timestamp, nr_ciclo_p bigint, dt_inicial_horario_p timestamp, dt_final_horario_p timestamp) FROM PUBLIC;
