CREATE OR REPLACE PROCEDURE adep_onc_gerar_horarios_pck.adep_onc_sincronizar_rec ( nm_usuario_p text, nr_atendimento_p bigint, ie_data_lib_prescr_p text, ie_exibir_suspensos_p text, ie_opcao_filtro_p text, dt_filtro_p timestamp, nr_ciclo_p bigint, dt_inicial_horario_p timestamp, dt_final_horario_p timestamp) AS $body$
DECLARE

				
	ds_sep_bv_w		varchar(50);
	nr_prescricao_w		bigint;
	nr_seq_recomendacao_w	integer;
	nr_seq_horario_w	bigint;
	ie_status_horario_w	varchar(1);
	cd_recomendacao_w	varchar(255);
	cd_intervalo_w		varchar(7);
	qt_recomendacao_w	double precision;
	ds_intervalo_w		varchar(255);
	ds_comando_update_w	varchar(4000);
	current_setting('adep_onc_gerar_horarios_pck.dt_horario_w')::timestamp		timestamp;
	nr_horario_w		integer;
	nr_ciclo_w		smallint;
	ds_dia_ciclo_w		varchar(5);
	nr_seq_onc_w		bigint;	
	dt_oncologia_w		timestamp;
	nr_seq_rec_cpoe_w	prescr_recomendacao.nr_seq_rec_cpoe%type;	
	
	c01 CURSOR FOR	
	SELECT	a.nr_prescricao,
		c.nr_seq_recomendacao,
		c.nr_sequencia,
		substr(obter_status_hor_rec(c.dt_fim_horario,c.dt_suspensao),1,1),
		coalesce(substr(coalesce(to_char(x.cd_recomendacao),x.ds_recomendacao),1,200),' ') cd_recomendacao,
		x.cd_intervalo,
		x.qt_recomendacao,
		substr(w.ds_prescricao,1,200) ds_prescricao,
		c.dt_horario,
		b.nr_ciclo,
		coalesce(b.ds_dia_ciclo, b.ds_dia_ciclo_real) ds_dia_ciclo,
		coalesce(x.nr_seq_ordem_adep,0) nr_seq_ordem_adep,
		coalesce(b.dt_real, b.dt_prevista) dt_oncologia,
		x.nr_seq_rec_cpoe
	FROM paciente_setor p, prescr_rec_hor c, paciente_atendimento b, prescr_medica a, prescr_recomendacao x
LEFT OUTER JOIN intervalo_prescricao w ON (x.cd_intervalo = w.cd_intervalo)
LEFT OUTER JOIN tipo_recomendacao y ON (x.cd_recomendacao = y.cd_tipo_recomendacao)
WHERE x.nr_prescricao = c.nr_prescricao and x.nr_sequencia = c.nr_seq_recomendacao and x.nr_prescricao = a.nr_prescricao and c.nr_prescricao = a.nr_prescricao and a.nr_seq_atend	= b.nr_seq_atendimento and b.nr_seq_paciente = p.nr_seq_paciente and a.nr_atendimento = nr_atendimento_p and p.cd_pessoa_fisica = a.cd_pessoa_fisica and coalesce(a.ie_recem_nato,'N')	= 'N' and ((ie_data_lib_prescr_p = 'F') or (obter_se_prescr_lib_adep(a.dt_liberacao_medico, a.dt_liberacao, a.dt_liberacao_farmacia, ie_data_lib_prescr_p) = 'S')) and ((ie_exibir_suspensos_p = 'S') or (coalesce(x.dt_suspensao::text, '') = '')) and coalesce(c.ie_situacao,'A') = 'A' and ((coalesce(c.ie_horario_especial,'N') = 'N') or (c.dt_fim_horario IS NOT NULL AND c.dt_fim_horario::text <> '')) and Obter_se_horario_liberado(c.dt_lib_horario, c.dt_horario) = 'S' and ((ie_opcao_filtro_p in ('Q','C')) or
		 ((ie_opcao_filtro_p = 'D') and (coalesce(b.dt_real,b.dt_prevista) between trunc(dt_filtro_p) and fim_dia(dt_filtro_p))) or
         (ie_opcao_filtro_p = 'H' AND c.dt_horario between dt_inicial_horario_p and dt_final_horario_p)) and b.nr_ciclo	= coalesce(nr_ciclo_p,b.nr_ciclo) group by
		c.dt_horario,
		a.nr_prescricao,
		c.nr_seq_recomendacao,
		c.nr_sequencia,
		c.dt_fim_horario,
		c.dt_suspensao,
		coalesce(substr(coalesce(to_char(x.cd_recomendacao),x.ds_recomendacao),1,200),' '),
		coalesce(to_char(x.cd_recomendacao),x.ds_recomendacao),
		coalesce(y.ds_tipo_recomendacao,x.ds_recomendacao),
		obter_se_acm_sn(x.ie_acm,x.ie_se_necessario),
		x.cd_intervalo,
		x.qt_recomendacao,
		x.ie_acm,
		w.ds_prescricao,
		b.nr_ciclo,
		coalesce(b.ds_dia_ciclo, b.ds_dia_ciclo_real),
		coalesce(x.nr_seq_ordem_adep,0),
		coalesce(b.dt_real, b.dt_prevista),
		x.nr_seq_rec_cpoe		
	order by
		c.dt_horario,
		c.dt_suspensao;
		
	
BEGIN
	ds_sep_bv_w 	:= obter_separador_bv;
	
	open c01;
	loop
	fetch c01 into	nr_prescricao_w,
			nr_seq_recomendacao_w,
			nr_seq_horario_w,
			ie_status_horario_w,
			cd_recomendacao_w,
			cd_intervalo_w,
			qt_recomendacao_w,
			ds_intervalo_w,
			current_setting('adep_onc_gerar_horarios_pck.dt_horario_w')::timestamp,
			nr_ciclo_w,
			ds_dia_ciclo_w,
			nr_seq_onc_w,
			dt_oncologia_w,
			nr_seq_rec_cpoe_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin		
		nr_horario_w := adep_onc_gerar_horarios_pck.get_posicao_horario( current_setting('adep_onc_gerar_horarios_pck.dt_horario_w')::timestamp );
		
		if (nr_horario_w IS NOT NULL AND nr_horario_w::text <> '') then
			ds_comando_update_w	:=	' update w_adep_t ' ||
							' set hora' || to_char(nr_horario_w) || ' = :vl_hora, ' ||
							' nr_prescricoes = adep_juntar_prescricao(nr_prescricoes,:nr_prescricao) ' ||
							' where nm_usuario = :nm_usuario ' ||
							' and ie_tipo_item = :ie_tipo ' ||
							' and nvl(nr_prescricao,nvl(:nr_prescricao,0)) = nvl(:nr_prescricao,0) ' ||
							' and nvl(nr_seq_item,nvl(:nr_seq_item,0)) = nvl(:nr_seq_item,0) ' ||					
							' and cd_item = :cd_item ' ||
							' and nvl(cd_intervalo,0) = nvl(:cd_intervalo,0) ' ||
							' and nvl(qt_item,0) = nvl(:qt_item,0) ' ||
							' and ((ds_prescricao = :ds_prescricao) or (ds_prescricao is null)) ' ||
							' and nr_ciclo = :nr_ciclo ' ||
							' and ds_dia_ciclo = :ds_dia_ciclo ' ||
							' and nr_seq_onc = :nr_seq_onc ' ||
							' and dt_oncologia = :dt_oncologia ' ||
							' and nvl(nr_seq_cpoe,nvl(:nr_seq_cpoe,0)) = nvl(:nr_seq_cpoe,0) ';
						
			CALL exec_sql_dinamico_bv('ADEP', ds_comando_update_w,	'vl_hora=S' || to_char(nr_seq_horario_w) || 'H' || ie_status_horario_w || ds_sep_bv_w ||
										'nr_prescricao=' || to_char(nr_prescricao_w) || ds_sep_bv_w ||
										'nm_usuario=' || nm_usuario_p || ds_sep_bv_w || 
										'ie_tipo=R' || ds_sep_bv_w ||
										'nr_seq_item='|| to_char(nr_seq_recomendacao_w) || ds_sep_bv_w ||
										'cd_item=' || to_char(cd_recomendacao_w) || ds_sep_bv_w ||
										'cd_intervalo=' || cd_intervalo_w  || ds_sep_bv_w ||
										'qt_item=' || to_char(qt_recomendacao_w) || ds_sep_bv_w ||
										'ds_prescricao=' || ds_intervalo_w || ds_sep_bv_w ||
										'nr_ciclo=' || to_char(nr_ciclo_w) || ds_sep_bv_w ||
										'ds_dia_ciclo=' || ds_dia_ciclo_w || ds_sep_bv_w ||
										'nr_seq_onc=' || to_char(nr_seq_onc_w) || ds_sep_bv_w ||
										'dt_oncologia=' || to_char(dt_oncologia_w,'dd/mm/yyyy hh24:mi:ss') || ds_sep_bv_w  ||
										'nr_seq_cpoe=' || to_char(nr_seq_rec_cpoe_w) || ds_sep_bv_w );
			commit;
		end if;
		end;
	end loop;
	close c01;
	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE adep_onc_gerar_horarios_pck.adep_onc_sincronizar_rec ( nm_usuario_p text, nr_atendimento_p bigint, ie_data_lib_prescr_p text, ie_exibir_suspensos_p text, ie_opcao_filtro_p text, dt_filtro_p timestamp, nr_ciclo_p bigint, dt_inicial_horario_p timestamp, dt_final_horario_p timestamp) FROM PUBLIC;
