CREATE OR REPLACE PROCEDURE ageint_sugerir_html5_pck.valida_regras_encaixe (nr_seq_proc_interno_p bigint, cd_estabelecimento_p bigint, cd_convenio_p bigint, cd_categoria_p text, hr_agenda_p timestamp, cd_agenda_p bigint, nr_seq_ageint_item_p bigint, ie_classif_agenda_p text, nm_usuario_p text, cd_medico_p bigint, ds_msg_erro_p INOUT text) AS $body$
DECLARE

cd_procedimento_w 		bigint;
ie_origem_proced_w		bigint;
cd_tipo_agenda_w			bigint;
cd_especialidade_item_w	integer;
nr_seq_regra_bloq_w 		bigint;
ds_msg_erro_w					varchar(4000);

	
BEGIN

		if (nr_seq_proc_interno_p IS NOT NULL AND nr_seq_proc_interno_p::text <> '') then
				obter_proc_tab_interno_conv(
					nr_seq_proc_interno_p,
					cd_estabelecimento_p,
					cd_convenio_p,
					cd_categoria_p,
					null,
					null,
					cd_procedimento_w,
					ie_origem_proced_w,
					null,
					hr_agenda_p,
					null,
					null,
					null,
					null,
					null,
					null,
					null,
					null);
			end if;

			select  max(cd_tipo_agenda)
			into STRICT    cd_tipo_agenda_w
			from    agenda
			where   cd_agenda = cd_agenda_p;

			select 	max(cd_especialidade)
			into STRICT	cd_especialidade_item_w
			from 	agenda_integrada_item
			where 	nr_sequencia = nr_seq_ageint_item_p;

			nr_seq_regra_bloq_w := obter_se_bloq_geral_agenda(cd_estabelecimento_p,
								cd_agenda_p,
								ie_classif_agenda_p, -- no caso de exames, ou ie_classif_item_w para consultas/servicos
								null, -- no caso de exames, ou null para consultas
								null, -- nao considerado pois nao esta indo para agenda_consulta
								obter_setor_regras_ageint(cd_tipo_agenda_w,cd_agenda_p,cd_procedimento_w,'S',nm_usuario_p,cd_estabelecimento_p),
								nr_seq_proc_interno_p,
								cd_procedimento_w,
								ie_origem_proced_w,
								cd_medico_p, -- verificar no insert o campo a ser utilizado
								hr_agenda_p, -- verificar no insert o campo a ser utilizado
								'N',
								'N');

			if (nr_seq_regra_bloq_w > 0) then
				ds_msg_erro_w := obter_mensagem_bloq_geral_age(nr_seq_regra_bloq_w);
				if (ds_msg_erro_w IS NOT NULL AND ds_msg_erro_w::text <> '') then
					ds_msg_erro_p := ds_msg_erro_w;
				end if;
			end if;

	END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
