CREATE OR REPLACE PROCEDURE aggregation_proc_managem_pck.gerar_agreg_surgery_info_daily (nr_seq_daily_report_p agreg_surgery_information.nr_seq_main_daily_rep%type ,cd_estabelecimento_p establishment_locale.cd_estabelecimento%type ,ie_main_flag_p agreg_surgery_information.ie_main_flag%type ,nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


cd_ward_w       agreg_main_daily_reports.cd_ward%type;
dt_referencia_w timestamp;
nr_sequencia_w  bigint;

c01 CURSOR FOR
SELECT cir.cd_pessoa_fisica cd_patient
      ,obter_idade_pf(atp.cd_pessoa_fisica, clock_timestamp(), 'A'/*'D'*/) qt_age
      ,obter_sexo_pf(atp.cd_pessoa_fisica, 'C') ie_gender
      ,cir.dt_inicio_real dt_start_surgery
      ,cir.nr_seq_proc_interno
      ,cir.cd_procedimento_princ
      ,cir.ie_origem_proced
from   cirurgia cir
      ,atend_paciente_unidade apu
      ,atendimento_paciente atp
where atp.nr_atendimento            = apu.nr_atendimento
and   apu.nr_atendimento            = cir.nr_atendimento
and   apu.cd_setor_atendimento      = cir.cd_setor_atendimento
and   establishment_timezone_utils.startOfDay(apu.dt_entrada_unidade) = dt_referencia_w
and   cir.cd_estabelecimento        = cd_estabelecimento_p;

BEGIN
  dt_referencia_w := trunc(clock_timestamp()) - 1;

  -- obter setor de atendimento
  select max(cd_ward)
  into STRICT   cd_ward_w
  from   agreg_main_daily_reports
  where  nr_sequencia       = nr_seq_daily_report_p
  and    cd_estabelecimento = cd_estabelecimento_p;

  for r01 in c01 
  loop
    select nextval('agreg_surgery_information_seq')
    into STRICT   nr_sequencia_w
;

    insert into agreg_surgery_information(nr_sequencia
           ,dt_atualizacao
           ,nm_usuario
           ,nm_usuario_nrec
           ,dt_atualizacao_nrec
           ,nr_seq_main_daily_rep
           ,dt_target
           ,cd_ward
           ,ie_classification
           ,cd_patient
           ,qt_age
           ,ie_gender
           ,dt_start_surgery
           ,cd_room
           ,nr_seq_proc_interno
           ,cd_procedimento_princ
           ,ie_origem_proced
           ,ie_main_flag )
    values (nr_sequencia_w
           ,clock_timestamp()
           ,nm_usuario_p
           ,nm_usuario_p
           ,clock_timestamp()
           ,nr_seq_daily_report_p
           ,clock_timestamp()
           ,cd_ward_w
           ,'9'
           ,r01.cd_patient
           ,r01.qt_age
           ,r01.ie_gender
           ,r01.dt_start_surgery
           ,' '
           ,r01.nr_seq_proc_interno
           ,r01.cd_procedimento_princ
           ,r01.ie_origem_proced
           ,ie_main_flag_p );
  end loop;
END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
