CREATE OR REPLACE FUNCTION ajusta_relatorio_swing_pck.obter_altura_bandas_padrao (nr_seq_relatorio_p bigint) RETURNS bigint AS $body$
DECLARE

	qt_soma_w		bigint := 0;
	qt_altura_banda_w	bigint;
	current_setting('ajusta_relatorio_swing_pck.nr_seq_banda_w')::bigint		bigint;

	C16 CURSOR FOR
		SELECT	nr_sequencia,
			qt_altura
		from	banda_relatorio
		where	nr_seq_relatorio 	= nr_seq_relatorio_p
		and	ie_tipo_banda		in ('C','R');
	
BEGIN
	open C16;
	loop
	fetch C16 into
		current_setting('ajusta_relatorio_swing_pck.nr_seq_banda_w')::bigint,
		qt_altura_banda_w;
	EXIT WHEN NOT FOUND; /* apply on C16 */
		begin
		qt_soma_w := qt_soma_w + qt_altura_banda_w;
		qt_soma_w := qt_soma_w + ajusta_relatorio_swing_pck.obter_altura_bandas_filhas(nr_seq_relatorio_p, current_setting('ajusta_relatorio_swing_pck.nr_seq_banda_w')::bigint);
		end;
	end loop;
	close C16;
	return	qt_soma_w;
	end;


$body$
