CREATE OR REPLACE FUNCTION ajusta_relatorio_swing_pck.obter_altura_pagina ( ie_tipo_papel_p text, ie_orientacao_p text, qt_altura_p bigint) RETURNS bigint AS $body$
DECLARE

	current_setting('ajusta_relatorio_swing_pck.qt_altura_papel_w')::bigint	bigint;
	
BEGIN

	if (ie_orientacao_p = 'R') then
		if (ie_tipo_papel_p = 'A4') then
			PERFORM set_config('ajusta_relatorio_swing_pck.qt_altura_papel_w', 842, false);
		elsif (ie_tipo_papel_p = 'A5') then
			PERFORM set_config('ajusta_relatorio_swing_pck.qt_altura_papel_w', 595, false);
		elsif (ie_tipo_papel_p = 'A6') then
