		elsif (ie_tipo_papel_p = 'Letter') then
			PERFORM set_config('ajusta_relatorio_swing_pck.qt_altura_papel_w', 792, false);
		elsif (ie_tipo_papel_p = 'Default') then
			PERFORM set_config('ajusta_relatorio_swing_pck.qt_altura_papel_w', 1008, false);
		elsif (ie_tipo_papel_p = 'B5') then
			PERFORM set_config('ajusta_relatorio_swing_pck.qt_altura_papel_w', 709, false);
		elsif (ie_tipo_papel_p = 'Env10') then
			PERFORM set_config('ajusta_relatorio_swing_pck.qt_altura_papel_w', 297, false);
		elsif (ie_tipo_papel_p = 'A3') then
