		elsif (ie_tipo_papel_p = 'Folio') then
			PERFORM set_config('ajusta_relatorio_swing_pck.qt_altura_papel_w', 1052, false);
		elsif (ie_tipo_papel_p = 'Custom') then
			PERFORM set_config('ajusta_relatorio_swing_pck.qt_altura_papel_w', (qt_altura_p / 2.54) * 75, false);
		else
			PERFORM set_config('ajusta_relatorio_swing_pck.qt_altura_papel_w', 842, false);
		end if;
	else --Paisagem
		if (ie_tipo_papel_p = 'A4') then
			PERFORM set_config('ajusta_relatorio_swing_pck.qt_altura_papel_w', 595, false);
