		elsif (ie_tipo_papel_p = 'Custom') then
			PERFORM set_config('ajusta_relatorio_swing_pck.qt_altura_papel_w', (qt_altura_p / 2.54) * 75, false);
		else
			PERFORM set_config('ajusta_relatorio_swing_pck.qt_altura_papel_w', 595, false);
		end if;
	end if;

	return current_setting('ajusta_relatorio_swing_pck.qt_altura_papel_w')::bigint;
	end;

