CREATE OR REPLACE FUNCTION alto_custo_artrite_pck.get_peso (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


	ds_retorno_w    varchar(255);

	
BEGIN
  
		select max(qt_peso)
		into STRICT   ds_retorno_w
		from (
			SELECT  asv.qt_peso qt_peso
			from    atendimento_sinal_vital asv
			where   asv.nr_atendimento = nr_atendimento_p
			and     (asv.dt_liberacao IS NOT NULL AND asv.dt_liberacao::text <> '')
			and     coalesce(asv.dt_inativacao::text, '') = ''
			and     asv.qt_peso between 15 and 299
			order by asv.dt_sinal_vital desc) alias3 LIMIT 1;

		return ds_retorno_w;

	END;

$body$
