CREATE OR REPLACE FUNCTION alto_custo_cancer_pck.get_cd_first_surgery (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


        ds_retorno_w			varchar(10);


BEGIN
        begin

            select  p.cd_procedimento_loc
            into STRICT    ds_retorno_w
            from    cirurgia c, 
                    procedimento p
            where   c.nr_atendimento = nr_atendimento_p
            and     c.ie_status_cirurgia = 2
            and     c.cd_procedimento_princ = p.cd_procedimento
            and     c.ie_origem_proced = p.ie_origem_proced
            and     c.dt_inicio_real = (
                                        SELECT  min(ci.dt_inicio_real)
                                        from    cirurgia ci
                                        where   ci.nr_atendimento = nr_atendimento_p
                                        and     ci.ie_status_cirurgia = 2
                                        and     ci.dt_inicio_real between alto_custo_pck.get_data_inicio_emissao_guia
                                                                    and alto_custo_pck.get_data_corte
                                        );

            exception
                when no_data_found then
                    ds_retorno_w := null;
        end;

      return ds_retorno_w;

    END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
