CREATE OR REPLACE FUNCTION alto_custo_cancer_pck.get_dt_exame_histop (cd_pessoa_fisica_p text, nr_atendimento_p bigint) RETURNS timestamp AS $body$
DECLARE


        ds_retorno_w			timestamp;


BEGIN

      begin

            select dt_estudo_diag 
            into STRICT    ds_retorno_w
            from (
                    SELECT  clr.dt_estudo_diag
                    from    diagnostico_doenca dd,
                            can_loco_regional clr,
                            regra_alto_custo_cid racc, 
                            regra_alto_custo rac 
                    where   clr.cd_pessoa_fisica = cd_pessoa_fisica_p
                    and     dd.nr_atendimento = nr_atendimento_p
                    and     clr.ie_tipo_estudo = 5
                    and     rac.ie_tipo_doenca = 'C'
                    and     rac.nr_sequencia = racc.nr_seq_regra_alto_custo
                    and     dd.cd_doenca like '%'||racc.cd_categoria_cid||'%'
                    and     clr.cd_doenca_cid = dd.cd_doenca
                    and     dd.ie_classificacao_doenca  = 'P'
                    and     dd.ie_tipo_diagnostico = 2
                    and     (clr.dt_liberacao IS NOT NULL AND clr.dt_liberacao::text <> '')
                    and     coalesce(clr.dt_inativacao::text, '') = ''
                    order by clr.dt_liberacao
                ) alias2 LIMIT 1;

        exception
            when no_data_found then
                ds_retorno_w := null;
      end;

      return ds_retorno_w;

    END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
