CREATE OR REPLACE FUNCTION alto_custo_cancer_pck.get_dt_first_quimio (cd_pessoa_fisica_p text) RETURNS timestamp AS $body$
DECLARE


        ds_retorno_w			timestamp;


BEGIN

        select  min(dt_inicio_adm)
        into STRICT    ds_retorno_w
        from    paciente_atendimento
        where   obter_pessoa_atendimento(nr_atendimento, 'C') = cd_pessoa_fisica_p
        and     nr_ciclo = 1
        and (ds_dia_ciclo = 'D0' or ds_dia_ciclo = 'D1')
        and     (dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
        and     coalesce(dt_suspensao::text, '') = ''
        and     (dt_inicio_adm IS NOT NULL AND dt_inicio_adm::text <> '')
        and     dt_inicio_adm between alto_custo_pck.get_data_inicio_emissao_guia
                                and alto_custo_pck.get_data_corte;

      return ds_retorno_w;

    END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
