CREATE OR REPLACE FUNCTION alto_custo_cancer_pck.get_dt_ult_reoperacao (nr_atendimento_p bigint) RETURNS timestamp AS $body$
DECLARE


        ds_retorno_w			timestamp;


BEGIN
        begin
            select max(DT_INICIO_REAL) dt_inicio_real 
            into STRICT ds_retorno_w
            from CIRURGIA
            where nr_atendimento = nr_atendimento_p 
                and ie_reoperacao = 'S';
            exception
                when no_data_found then
                    ds_retorno_w := null;
        end;

      return ds_retorno_w;

    END;

$body$
