CREATE OR REPLACE FUNCTION alto_custo_cancer_pck.get_if_suporte_nutricional (nr_atendimento_p text) RETURNS bigint AS $body$
DECLARE


      ds_retorno_w			bigint;


BEGIN

        select (case 
                when enteral > 0 and parenteral = 0 then 1
                when enteral = 0 and parenteral > 0 then 2
                when enteral > 0 and parenteral > 0 then 3
                else 4 end
                ) tipo_dieta
        into STRICT    ds_retorno_w
        from (
                SELECT  sum(case when ie_tipo_dieta = 'E' then 1 else 0 end) enteral,
                        sum(case when(ie_tipo_dieta = 'P' or ie_tipo_dieta = 'I') then 1 else 0 end) parenteral
                from    cpoe_dieta
                where   nr_atendimento = nr_atendimento_p
                and     (dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
                and     coalesce(dt_suspensao::text, '') = ''
                and     dt_inicio between alto_custo_pck.get_data_inicio_emissao_guia
                                        and alto_custo_pck.get_data_corte
                ) alias6;

        return ds_retorno_w;

    END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
