CREATE OR REPLACE FUNCTION alto_custo_cancer_pck.get_is_nutrician (nr_atendimento_p text) RETURNS bigint AS $body$
DECLARE


      ds_retorno_w			bigint;


BEGIN
        begin
            select CASE WHEN count(ep.cd_evolucao)=0 THEN  2  ELSE 1 END 
            into STRICT ds_retorno_w
            from evolucao_paciente ep, usuario u
            where ep.nm_usuario = u.nm_usuario
            and u.CD_PESSOA_FISICA = ep.cd_medico
            and nr_atendimento = nr_atendimento_p
            and u.IE_TIPO_EVOLUCAO = 4
            and (ep.dt_liberacao IS NOT NULL AND ep.dt_liberacao::text <> '')
            and coalesce(ep.dt_inativacao::text, '') = ''
            and dt_evolucao between alto_custo_pck.get_data_inicio_emissao_guia
            and alto_custo_pck.get_data_corte;
        exception
            when no_data_found then
                ds_retorno_w := null;
        end;

      return ds_retorno_w;

    END;

