CREATE OR REPLACE FUNCTION alto_custo_cancer_pck.get_qt_cirurgia (nr_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE


      ds_retorno_w    integer;


BEGIN

        select  count(*)
        into STRICT    ds_retorno_w
        from    cirurgia
        where   nr_atendimento = nr_atendimento_p
        and     ie_status_cirurgia = 2
        and     dt_inicio_real between alto_custo_pck.get_data_inicio_emissao_guia
                                    and alto_custo_pck.get_data_corte;

        return ds_retorno_w;

    END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
