CREATE OR REPLACE FUNCTION alto_custo_erc_pck.get_causa_morte ( cd_pessoa_fisica_p text ) RETURNS bigint AS $body$
DECLARE

        ds_retorno_w bigint;

BEGIN
        begin
                select  CASE WHEN                     ie_doenca_cronica_mx='RENAL' THEN  1 WHEN                     ie_doenca_cronica_mx='CARDIO' THEN  2 WHEN                     ie_doenca_cronica_mx='CACUI' THEN  3 WHEN                     ie_doenca_cronica_mx='MACMA' THEN  4 WHEN                     ie_doenca_cronica_mx='INFST' THEN  4  ELSE 5 END 
        into STRICT    ds_retorno_w
        from    cid_doenca a,
                declaracao_obito b,
                atendimento_paciente c
        where   b.cd_cid_direta = a.cd_doenca_cid
        and     c.nr_atendimento = b.nr_atendimento
        and     b.ie_situacao = 'A'
        and     c.cd_pessoa_fisica = cd_pessoa_fisica_p;

        exception
        when no_data_found then
            ds_retorno_w := 98;
        end;

        return ds_retorno_w;
    END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
