CREATE OR REPLACE FUNCTION alto_custo_erc_pck.get_dt_inicio_tratamento ( cd_pessoa_fisica_p text ) RETURNS timestamp AS $body$
DECLARE

         ds_retorno_w timestamp;

BEGIN
    
        select  max(dt_inicio_tratamento)
        into STRICT    ds_retorno_w
        from    paciente_tratamento
        where   cd_pessoa_fisica = cd_pessoa_fisica_p
		and 	dt_inicio_tratamento <= alto_custo_pck.get_data_corte;

      return ds_retorno_w;
    END;

$body$
LANGUAGE PLPGSQL
