CREATE OR REPLACE FUNCTION alto_custo_erc_pck.get_dt_ret_dial_perda_tr_renal ( cd_pessoa_fisica_p text ) RETURNS timestamp AS $body$
DECLARE

        ds_retorno_w timestamp;

    
BEGIN
    
        select  MIN(dt_perda_orgao)
        into STRICT    ds_retorno_w
        from    TX_RECEPTOR 
        where   cd_pessoa_fisica = cd_pessoa_fisica_p
        and     (dt_perda_orgao IS NOT NULL AND dt_perda_orgao::text <> '');

      return ds_retorno_w;
    END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
