SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION alto_custo_erc_pck.get_dt_retorno_dialise ( cd_pessoa_fisica_p text ) RETURNS timestamp AS $body$
DECLARE

         ds_retorno_w timestamp;

BEGIN
    
        select  min(dt_inicio_tratamento)
        into STRICT    ds_retorno_w
        from    tx_receptor a,
                tx_orgao b,
                paciente_tratamento c
        where a.nr_seq_orgao = b.nr_sequencia
        and a.cd_pessoa_fisica = c.cd_pessoa_fisica
        and a.cd_pessoa_fisica = cd_pessoa_fisica_p 
        and b.ie_orgao = 'RIM'
        and (a.dt_perda_orgao IS NOT NULL AND a.dt_perda_orgao::text <> '')
        and dt_inicio_tratamento > dt_perda_orgao
        order by dt_inicio_tratamento asc;

      return ds_retorno_w;
    END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
