CREATE OR REPLACE FUNCTION alto_custo_erc_pck.get_pac_tem_transpl_renal ( cd_pessoa_fisica_p text ) RETURNS bigint AS $body$
DECLARE

        ds_retorno_w bigint;

BEGIN

        select  coalesce(min(1), 5)
        into STRICT    ds_retorno_w
        from    tx_registro_snt a,
                tx_receptor b
        where   a.nr_seq_receptor = b.nr_sequencia
        and     b.nr_seq_orgao in (SELECT nr_sequencia
                                     from tx_orgao c
                                    where c.ie_orgao = 'RIM')
        and     b.cd_pessoa_fisica = cd_pessoa_fisica_p;

      return ds_retorno_w;
    END;

