SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION alto_custo_erc_pck.get_qt_transp_renal_pac ( cd_pessoa_fisica_p text ) RETURNS bigint AS $body$
DECLARE

        ds_retorno_w bigint;

BEGIN

        select  count(1)
        into STRICT    ds_retorno_w
        from    tx_receptor b
        where   b.cd_pessoa_fisica = cd_pessoa_fisica_p
        and     b.nr_seq_orgao in (SELECT nr_sequencia
                                    from tx_orgao c
                                   where c.ie_orgao = 'RIM')
        and     (b.dt_transplante IS NOT NULL AND b.dt_transplante::text <> '');

      return ds_retorno_w;
    END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
