SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION alto_custo_erc_pck.get_tipo_atendimento ( cd_pessoa_fisica_p text ) RETURNS bigint AS $body$
DECLARE

        ds_retorno_w atendimento_paciente.ie_tipo_atendimento%type;

BEGIN
        begin
            select ie_tipo_atendimento
            into STRICT    ds_retorno_w
            from (
                SELECT  CASE WHEN b.ie_tipo_atendimento=8 THEN  2  ELSE 1 END  ie_tipo_atendimento
                from    paciente_tratamento a,
                        atendimento_paciente b
                where   a.nr_atend_origem = b.nr_atendimento
                and     b.ie_tipo_atendimento in (1,8)
                and     a.dt_inicio_tratamento between b.dt_entrada and coalesce(b.dt_alta, clock_timestamp())
                and     b.cd_pessoa_fisica = cd_pessoa_fisica_p
				and 	a.dt_inicio_tratamento <= alto_custo_pck.get_data_corte
                order by a.dt_inicio_tratamento asc) alias3 LIMIT 1;

        exception
            when no_data_found then
                ds_retorno_w := 98;
        end;

      return ds_retorno_w;
    END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
