SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION alto_custo_erc_pck.get_tipo_doador_pac ( cd_pessoa_fisica_p text ) RETURNS bigint AS $body$
DECLARE

        ds_retorno_w bigint;

BEGIN

        select  CASE WHEN max(d.ie_tipo_doador)='CAD' THEN  1 WHEN max(d.ie_tipo_doador)='VIV' THEN  2  ELSE 98 END 
        into STRICT    ds_retorno_w
        from    tx_receptor b,
                tx_doador_potencial c,
                tx_doador d
        where   b.cd_pessoa_fisica = cd_pessoa_fisica_p
        and     c.nr_seq_receptor = b.nr_sequencia
        and     c.ie_doador_efetivo = 'S'
        and     d.nr_sequencia = c.nr_seq_doador
        and     b.nr_seq_orgao in (SELECT nr_sequencia
                                     from tx_orgao c
                                    where c.ie_orgao = 'RIM')
        and     (b.dt_transplante IS NOT NULL AND b.dt_transplante::text <> '') 
        order by b.dt_transplante desc LIMIT 1;

      return ds_retorno_w;
    END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
