CREATE OR REPLACE FUNCTION alto_custo_erc_pck.get_ultima_dial_peritoneal ( cd_pessoa_fisica_p text ) RETURNS bigint AS $body$
DECLARE

         ds_retorno_w atendimento_paciente.ie_tipo_atendimento%type;

BEGIN
        begin
            select ie_tratamento
            into STRICT    ds_retorno_w
            from (
                SELECT  case 
                            when ie_tratamento = 'CAPD' then 1
                            when ie_tratamento in ('CPI', 'DPA', 'DPAV') then 2
                            else 98 
                        end ie_tratamento        
                from    paciente_tratamento
                where   cd_pessoa_fisica = cd_pessoa_fisica_p
				and 	trunc(dt_inicio_tratamento) >= add_months(trunc(alto_custo_pck.get_data_corte), -3)
				and     trunc(dt_inicio_tratamento) <= trunc(alto_custo_pck.get_data_corte)
                order by dt_inicio_tratamento desc) alias6 LIMIT 1;

        exception
            when no_data_found then
                ds_retorno_w := 98;
        end;

      return ds_retorno_w;
    END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
