CREATE OR REPLACE FUNCTION alto_custo_erc_pck.get_ultima_dose_dialise ( nr_atendimento_p bigint ) RETURNS bigint AS $body$
DECLARE

         ds_retorno_w hd_controle.qt_ktv%type;

BEGIN
        begin
            select qt_ktv
            into STRICT    ds_retorno_w
            from (
                SELECT  b.qt_ktv qt_ktv
                from    hd_dialise a,
                        hd_controle b
                where   a.nr_sequencia = b.nr_seq_dialise
                and     a.nr_atendimento = nr_atendimento_p
				and 	trunc(a.dt_dialise) >= add_months(trunc(alto_custo_pck.get_data_corte), -3)
				and     trunc(a.dt_dialise) <= trunc(alto_custo_pck.get_data_corte)
                order by b.dt_controle  desc) alias5 LIMIT 1;
        exception
            when no_data_found then
                ds_retorno_w := 98;
        end;

      return ds_retorno_w;
    END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
