CREATE OR REPLACE FUNCTION alto_custo_hemofilia_pck.get_classif_gravidad_nivel_fat (nr_atendimento_p atendimento_paciente.nr_atendimento%type) RETURNS varchar AS $body$
DECLARE

    ds_retorno_w  diagnostico_classif_adic.cd_externo%type;

BEGIN
    select  coalesce(max(dc.cd_externo), '9999')
    into STRICT    ds_retorno_w
    from    diagnostico_doenca dd,
            diagnostico_classif_adic dc
    where   dc.nr_sequencia = dd.nr_seq_classif_adic
    and     dd.nr_atendimento = nr_atendimento_p
    and     alto_custo_pck.is_alto_custo(dd.cd_doenca, current_setting('alto_custo_hemofilia_pck.ie_tipo_doenca_w')::regra_alto_custo.ie_tipo_doenca%type) = 'S';

    return ds_retorno_w;
  END;

$body$
