CREATE OR REPLACE FUNCTION alto_custo_hemofilia_pck.get_deficiencia_diagnosticada () RETURNS varchar AS $body$
DECLARE

    ds_retorno_w  diagnostico_classif_adic.cd_externo%type;

BEGIN
    begin
      select  t.ie_tipo_deficiencia
      into STRICT    ds_retorno_w
      from (
        SELECT  c.ie_tipo_deficiencia
        from    table(alto_custo_pck.get_atendimentos_paciente(current_setting('alto_custo_hemofilia_pck.option_last_w')::varchar(1))) t,
                diagnostico_doenca d,
                cid_doenca c
        where   d.nr_atendimento = t.nr_atendimento
        and     c.cd_doenca = d.cd_doenca
        and     c.ie_doenca_cronica_mx = 'HEMOF'
        and     (c.ie_tipo_deficiencia IS NOT NULL AND c.ie_tipo_deficiencia::text <> '')
        order by
                d.nr_atendimento desc
      ) t LIMIT 1;
    exception
      when no_data_found then
        ds_retorno_w := '';
    end;

    return ds_retorno_w;
  END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
