SET client_encoding TO 'UTF8';




CREATE OR REPLACE FUNCTION alto_custo_hemofilia_pck.get_hemofilia_cronica () RETURNS varchar AS $body$
DECLARE

    cid_hemof_cronica_w constant varchar(3) := 'M36';
    ds_retorno_w        varchar(1);

BEGIN
    select  coalesce(max('1'), '0')
    into STRICT    ds_retorno_w
    from    table(alto_custo_pck.get_atendimentos_paciente(current_setting('alto_custo_hemofilia_pck.option_all_w')::varchar(1))) t,
            diagnostico_doenca d,
            cid_doenca c
    where   d.nr_atendimento = t.nr_atendimento
    and     c.cd_doenca = d.cd_doenca
    and (c.cd_doenca = cid_hemof_cronica_w or c.cd_categoria_cid = cid_hemof_cronica_w)
    and     c.ie_doenca_cronica_mx = 'AHEMC';

    return ds_retorno_w;
  END;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
